INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1, 'Light American Wheat Ale or Lager', 'Hocus Pocus', 4.5, 812, 'Our take on a classic summer ale.  A toast to weeds, rays, and summer haze.  A light, crisp ale for mowing lawns, hitting lazy fly balls, and communing with nature, Hocus Pocus is offered up as a summer sacrifice to clodless days.\n\n\nIts malty sweetness finishes tart and crisp and is best apprediated with a wedge of orange.', 1);
