INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1008, 'American-Style Pale Ale', 'Ironbound Ale', 5.0999999046, 700, 'Hold onto your beer mugs, because this medium-bodied beer has been overwhelmed with hop additions. Brewed as an American pale ale, it\'s a celebration of American hop varieties: Cascade, Crystal, Centennial and Chinook. You won\'t be disappointed.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1010, 'Extra Special Bitter', 'Anvil Ale', 4.8000001907, 700, 'English style best bitter. It is a traditional English pub ale that is easy drinking, often referred to as a session beer\". It is medium-bodied and copper colored with a noticeable malt flavor up front that finishes with a hop bitterness and floral hop flavor.\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1013, 'Scotch Ale', 'Dirty Bastard Scotch Style Ale', 8.5, 549, 'Founders flagship beer. Dirty Bastard is an absolute beautiful beer to behold. Dark ruby in color and brewed with ten varieties of imported malts this beer continuously lives up to its reputation as a bold and powerful ale. Dirty Bastard is complex in the finish with hints of smoke and peat paired with a malty richness, finalized with a good bit of hop attitude.  This beer ain\'t for the wee lads.', 1);
