INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1238, 'American-Style Amber/Red Ale', 'Double Barrel Ale', 5, 524, 'A British Pale Ale never tasted so fresh. We challenge our good friends across the pond to match this one. Canâ€™t be done. Weâ€™ve honored the traditions of the great British Pale brewers of Burton-on-Trent using our patented Firestone Union oak barrels. Youâ€™re left with a mild blend of vanilla and toasted oak flavor touched with an elegant hint of English noble hops. DBA is the flagship of our company and wildly popular.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1239, 'American-Style India Pale Ale', 'Island Pale Ale (IPA)', 7, 404, 'Our Islander IPA is a powerful West Coast IPA. This beer bites back with an intense hoppy zing and high alcohol content. The Islander IPA is dry hopped with tons of Centennial & Chinook hops and its fruity aroma will win your approval.', 1);
