INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1402, 'American-Style Brown Ale', 'Downtown Brown', 5, 803, 'A smooth, full-bodied nut brown ale, lightly hopped with a hint of roasted chocolate and crystal malts.  This ale is dark in color without the heavy taste of porter or stout.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1416, 'German-Style Oktoberfest', 'Sprecher Oktoberfest', 5.75, 1181, 'Traditionally brewed to celebrate the harvest season, this reddish-brown lager has a rich caramel character and a long flavorful finish. Its delicious malty sweetness is nicely accented by a slighty fruity bouquet and a mild hop flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1425, 'Imperial or Double India Pale Ale', 'Hop Rod Rye', 8, 94, 'Essentially a strong American IPA made with 20% rye malt. Darker in color, Hop Rod Rye boasts a huge hop aroma and flavor accompanied by a slightly sweet, malty finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1431, 'South German-Style Hefeweizen', 'Saranac Hefeweizen', 5.5999999046, 512, 'This traditional style German Wheat beer (pronounced) Hah-fuh-vite-zen) remains unfiltered to preserve it\'s natural smooth flavor and aroma. Don\'t be alarmed by its cloudy appearance or noticeable sediment. The slight banana and clove aroma comes from the authentic German Hefeweizen yeast. It has a light refreshing finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1432, 'Porter', 'Chief Blackhawk Porter', 5.5999999046, 1301, 'In 1767 a great Sauk leader was born.  His name meant the black sparrow hawk.\"  He came to be known as Black Hawk.  Strong beliefs  independent thinking and an unwavering commitment to his family and his people earned him a reputation as a man of integrity and courage.  In 1832Chief BlackHawk Porter is a robust black and sharply bittersweet ale.  This style was traditionally the session beer consumed by the porters in London.\"', 1);
