INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1476, 'Other Belgian-Style Ales', 'Dubbel Dragon', 7.1999998093, 463, 'This Dubbel style Belgian beer is created in the great Belgian tradition. Pale Malt, Caramel and Special B Malts impart a unique sweetness to this beer. The traditional Belgian yeast fully ferments the Belgian Candi Sugar to give a satisfying taste that is twice what a Belgian pale delivers.', 1);
