INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1711, 'Traditional German-Style Bock', 'Winter Brew', 5.75, 1181, 'A flavorful blend of dark roasted and sweet caramel malts defines this smooth and robust lager. The rich, nourishing flavors of a full-bodied Munich bock make this Bavarian-style brew perfect for those long winter nights.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1712, 'German-Style Schwarzbier', 'Black Bavarian Lager', 5.8600001335, 1181, 'This intensely dark Kulmbacher style lager has a superb malt complexity with the distinctive flavors and aromas of coffee, caramel and chocolate.\n\n\nA renowned smoothness and a creamy, tan head make it a world champion.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (1714, 'Porter', 'Donner Party Porter', 6.6999998093, 520, 'Sustenance to get you through those long, cold winters, the Donner Party Porter is another of FiftyFifty\'s flagship beers. Reminiscent of dark chocolate, espresso, and dark dried fruits with a long complex finish, this beer is good for one of those nights where you\'ve dug yourself into a snow bank because the ski trip didn\'t go quite as planned. Deep brown with mahogany highlights, the hops and malt are very well balanced leaving one to contemplate the myriad of flavors as the beer warms. Moderate to slightly heavy, this beer is heaven in a glass.', 1);
