INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (2003, 'German-Style Heller Bock/Maibock', 'Sprecher Maibock', 6, 1181, 'Pale malt flavors are balanced with a rich hop character and a light fruit bouquet in this seasonal spring lager. Traditional dry-hopping and extended aging give this blonde bock a distinctive flowery aroma and a potent kick.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (2008, 'American-Style Barley Wine Ale', 'John Barleycorn Barleywine Style Ale', 9, 810, 'Our Seasonal Offering. We honor the Harvest & Holidays each year by brewing a traditional Barleywine style ale, using a variety of ingredients. The Label, created by local artist Janis Taylor, is a folksy woodcut based on the tale of John Barleycorn in verse.\n\n\nBrewed in small 10-barrel batches with Certified Organic barley malt, this crimson hued ale has a sweet caramel nose with a zesty spicy, citrus taste. It has a slight hop finish and leaves a warm, tingling sensation on the tongue. A great winter warmer!\n\nStarting Gravity 	1.098\n\nFinish Gravity 	1.020\n\nABV 	9.5\n\nIBU 	96\n\n\n-http://www.madriverbrewing.com/pages/brews/john_barleycorn.html', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (2011, 'German-Style Doppelbock', 'Butthead Doppelbock', 8.1999998093, 1271, 'High Gravity Butt Head Doppelbock Lager is brewed with a generous amount of roasted malts producing caramel sweetness and rich mouthfeel. Why Butt Head? Try one. You\'ll enjoy big brew\" flavor with the intensity of a \"head butting\" Bighorn Ram!\"', 1);
