INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (2561, 'Scottish-Style Light Ale', 'Dark Island', 4.5999999046, 953, 'Dark Island is an iconic beer: a standard-bearer for traditional Scottish ales. In cask, this beer has twice won CAMRAâ€™s Champion Beer of Scotland.\n\nOn the nose, this dark beer offers bitter chocolate, figs, toffee and hints of fruit.\n\n\nOn the palate, this resolves into beautiful, silky-smooth, coffee-and-chocolate flavours, followed by figs, dates and dried fruits, with a very appealing, lingering aftertaste of fruits and hop bitterness.', 1);
