INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (2985, 'Imperial or Double India Pale Ale', 'Obliteration VI', 8.5, 858, 'With passion & purpose, we present this series of experimental hop-driven beers. Using different hop varieties & brewing techniques, we aim to educate the palate & challenge the hophead in you.\n\n\nObliteration VI is a Double IPA brewed with Summit, Summit and Summit; 95 IBUâ€™s', 1);
