INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3106, 'American-Style Brown Ale', 'Saint Arnold Brown Ale', 5.3000001907, 1091, 'A beautiful, deep copper brown ale. It has a full, malty body with hints of chocolate, a touch of sweetness and a light hop flavor. A complex malt character is created by combining five different types of malts. It has a rich, creamy head with a fine lace. The light fruitiness, characteristic of ales, is derived from a proprietary yeast strain. \n\n\nSaint Arnold Brown Ale is best consumed at 45-50Â° Fahrenheit.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3108, 'Scotch Ale', 'Skullsplitter Ale', 8.5, 953, 'Skull Splitter is our strongest ale: which is named after Thorfinn Einarsson who was the 7th Viking Earl of Orkney. Sophisticated, satiny smooth with a deceptively light character, it is a tribute to our colourful forbear.\n\n\nOn the nose, this strong beer has a fruity malt character, with hints of dark fruit, spicy hop, dates and figs.\n\n\nOn the palate, rich and complex with sweet toasted malt, molasses, fresh and dried fruit and hints of warming spices.', 1);
