INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3350, 'Winter Warmer', 'Jubelale', 6.6999998093, 441, 'Did you know that Jubelale was the first beer ever bottled by Deschutes Brewery?  Highly anticipated every fall, Jubelale is in a category of its own with a flavor and following that is impossible to match.  Dark crystal malt creates that â€œlusciousâ€ holiday note while the roasty flavor and bountiful hops excite your tastebuds, reminding you why Jubelale is the perfect holiday beer', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3352, 'American-Style Pale Ale', 'Harvest Ale', 6.5, 1142, 'The cornerstone of our Harvest series is the beer that started the modern-day fresh hop ale phenomenon in America, our original Harvest Ale. \n\n\nCreated in 1996, Harvest Ale features Cascade and Centennial hops from the Yakima Valley in Eastern Washington. These hops are harvested and shipped as â€œwetâ€ un-dried hopsâ€”the same day they are pickedâ€”to our brewery in Chico where our brewers eagerly wait to get them into the brew kettle while their oils and resins are still at their peak.', 1);
