INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3559, 'Golden or Blonde Ale', 'Base Camp Golden Ale', 4.9000000954, 520, 'The lightest of FiftyFifty\'s ales, Base Camp is a thirst quenching delight. Pale straw to light gold in color, this beer has a mild malt flavor combined with light bitterness and hop flavor. This beer finishes dry and is very light and refreshing on the palate. Base Camp is a session beer\"  meaning you can easily toss back a few in one sitting. So go ahead', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3566, 'Belgian-Style Dubbel', 'Affligem Dubbel', 6.8000001907, 12, 'A reddish-brown abbey ale brewed with dark malts. The secondary fermentation gives a fruity aroma and a unique, spicy character with a distinctive aftertaste. Secondary fermentation in the bottle. Contains barley malt.', 1);
