INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3587, 'American-Style Pale Ale', 'Mirrorpond Pale Ale', 5, 441, 'Mirror Pond is just a short walk from the Deschutes Brewery & Public House in downtown Bend and reflects the Three Sisters Mountains. This scenic spot alongside the Deschutes River is the localsâ€™ choice for summer festivals and concerts.', 1);
