INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3621, 'Old Ale', 'Snow Cap', 6.9000000954, 1031, 'A rich, full-bodied winter warmer crafted in the British tradition of holiday beers. This deep mahogany colored brew balances complex fruit flavors with a refreshingly smooth texture, making Snow Cap a highly drinkable and desirable cold weather companion.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3622, 'American-Style Stout', 'Obsidian Stout', 6.4000000954, 441, 'Obsidian Stout gets is inspiration from one of the world\'s largest obsidian flows at Newberry Volcano--just a few miles south of the brewery.  The Big Obsidian Flow  as they call it, covers more than 700 acres with shiny black obsidian.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3624, 'American-Style Pale Ale', 'Cascade Golden Ale', 4.5, 441, 'Gracing the expansive Western skyline in Central Oregon, the Three Sistersâ€”Faith, Hope and Charityâ€”are three prominent peaks in the Cascade mountain range.  Local folklore credits the naming to 19th Century fur trappers.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3629, 'Extra Special Bitter', 'Bachelor ESB', 5.3000001907, 441, 'Standing alone, distant from the Three Sisters mountains nestled to the north, Bachelor Butte was originally called Brother Jonathan\" and then simply \"The Bachelor\" before becoming widely known today as Mt. Bachelor.\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3647, 'American-Style Pale Ale', 'Tall Tale Pale Ale', 4.9000000954, 333, 'Big and bold, our Pale Ale distinguishes itself by its huge, fresh hop aroma and flavor. A combination of Cascade and Centennial hops, both in the kettle and during an extensive dry-hopped conditioning time, gives this beer its nectarious flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3649, 'Porter', 'Charles River Porter', 6, 333, 'Charles River Porter\n\n\nDeep and dark, full-bodied and robust, our Porter is crammed full of rich, roasted malt character. With underlying notes of fruitness, caramel and toffee, it finishes with a strong hop presence', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (3650, 'American-Style Amber/Red Ale', 'Cambridge Amber', 4.3000001907, 333, 'Well balanced, medium-bodied, with a deep amber-red color, this beer\'s complex palate covers all the bases. A malty caramel sweetness is followed by notes of chocolate and a dry, slightly roasty finish, complemented by a touch of fruity, spicy hops.', 1);
