INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4238, 'American-Style Pale Ale', 'Avenue Ale', 4.0999999046, 1357, '2007 Maryland Governor\'s Cup Bronze Medal:\n\nA golden colored ale with crisp malt character and a slightly spicy hop aroma. We use a good amount of wheat and some German malt in this one to give it a very refreshing taste. Avenue Ale is a true session beer, so sit down and prepare for a long one.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4240, 'Special Bitter or Best Bitter', 'Geary\'s Pale Ale', 4.5, 423, 'A classic British pale ale with a nod to the legendary beers of Burton-on-Trent. Copper-colored', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4241, 'Traditional German-Style Bock', 'Aecht Schlenkerla Rauchbier Urbock', 6, 650, 'A smoked bock beer for Bamberg\'s strong beer season (October through December). Matured for months in ancient rock-cellars underneath Bamberg and tapped freshly from the oakwood cask. Similar to, but much bigger than the classic Maerzen style.', 1);
