INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4243, 'Light American Wheat Ale or Lager', 'Grolsch Premium Weizen', 5.5, 616, 'Grolsch Premium Weizen is the main wheat beer made by Grolsch.\n\nIt is made according to the German Reinheitsgebot (German Beer Purity Law\")', 1);
