INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4247, 'Dark American-Belgo-Style Ale', 'Sneck Lifter', 5.0999999046, 713, 'A dark beer with a reddish tinge derived from the use of coloured malts, full of complex flavours, which create an intriguing beer of great character.\n\n\nIn Northern English dialect sneck means door latch and a sneck lifter was a manâ€™s last sixpence which enabled him to lift the latch of a pub door and buy himself a pint, hoping to meet friends there who might treat him to one or two more.', 1);
