INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4256, 'French & Belgian-Style Saison', 'Domaine DuPage French Country Ale', 5.9000000954, 1298, 'Domaine DuPage is a rural, northern France, amber colored ale. This well balanced beer is full and sweet up front with caramel, toasty, and fruity characters. The finish has a gentle floral and spicy hop balance that cleanses the palate.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4257, 'American-Style Pale Ale', 'The Bitter End Pale Ale', 5.0999999046, 1298, 'Very drinkable light golden ale.  Extremely refreshing hop finish', 1);
