INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4260, 'American-Style Lager', 'Honey Brown', 4.5, 656, 'From the Brewer\'s Website:\n\nThe Dundee family began with Honey Brown Lager in the early 1990\'s during the height of the craft brewing resurgence. Introduced by then brewery head Jack JW\" Wehle', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4261, 'South German-Style Hefeweizen', 'Apricot Weizen Ale', 5.0999999046, 1031, 'Brewed by the original Wheat Beer Pioneers, Pyramid Apricot Weizen Ale is left unfiltered for extra flavor and aroma.\n\n\nThe gold medalist of fruit beers, Pyramid Apricot Weizen is an adventurous wheat ale that offers the pleasing aroma and flavor of fresh apricots, and smooth and refreshing character for which our wheat beers are known.\n\n\nOriginal Gravity: 11.75\n\nAlcohol By Volume: 5.10%\n\nMalts: 2-Row Barley, Malted Wheat, Caramel\n\nHops: Nugget\n\nAvailability: Year Round\n\nBest Paired With: Appetizers, salads, and desserts such as pies and pastries.\n\n\nBest of the Northwest/Pacific in the Fruit Beer\" category at the 2000 United States Beer Tasting Championship', 1);
