INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4263, 'Light American Wheat Ale or Lager', 'St-Ambroise Apricot Wheat Ale', 5, 198, 'Apricot Wheat Ale blends various barley malts with malted wheat and natural apricot essence to create an original-tasting beer with a clean, fruit nose.\n\n\n5% alc/vol  Available in Bottles and Draft', 1);
