INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4282, 'Belgian-Style White', 'Allagash White', 5, 23, 'Our interpretation of a traditional Belgian wheat beer, Allagash White is unique and truly refreshing. Brewed with a generous portion of wheat and our own special blend of spices, this beer is light and slightly cloudy in appearance, with a spicy aroma. Overall, it is a beer that is very drinkable and smooth any time of the year.\n\n\n\n\nAvailable in: 12 oz and 750 ml bottles, 15.5 and 5.17 gal kegs\n\nABV: 5.0%\n\nOriginal Gravity: 1048\n\nRecommended Serving Temp: 34Â°F to 50Â°F', 1);
