INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4318, 'American-Style Lager', 'Duveneck\'s Dortmunder', 4.5, 83, 'Frank Duveneck was a famous Cincinnati painter who produced such masterpieces as Whistling Boy andOld Man In a Fur Cap. Our Dortmunder is a medium- bodied golden lager showcasing a delicious malt profile balanced with Noble German hops. This beer is designed to satisfy even the stodgiest of old beer curmudgeons!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4319, 'American-Style Pale Ale', 'Cumberland Pale Ale', 4.5999999046, 83, 'Early Cincinnati brewers shipping their beers into Tennessee would generously hop their beer to prevent spoilage during the long arduous trip over the Cumberland Trail. This classic interpretation of the American Pale Ale features a solid foundation of pale malt loaded with fresh Cascade hops. Hereâ€™s to getting where youâ€™re going!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4320, 'American-Style Amber/Red Ale', 'RedLegg Ale', 4.9000000954, 83, 'In the old days, runners who made it to third base were rewarded with a mug of beer! This is the way the game was meant to be played; pitchers throwing side- armed and a half barrel on the field. Our Red Legg Ale is an American Amber Ale with a rich, malty flavor, slightly sweet on the finish. An All-Star in any line- up!', 1);
