INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4353, 'American-Style India Pale Ale', 'Traditional IPA', 5.9000000954, 801, 'The first IPAs were unfiltered and featured extra hops and higher strength as a preservative for the long trip from England to the colony of India. Our Traditional IPA is naturally carbonated, dry-hopped & unfiltered like the old days.', 1);
