INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4360, 'Belgian-Style White', 'Belgian White', 4.6999998093, 801, 'Our Belgian White Ale is modeled after the original Belgian Witbiers brewed in monasteries during the early 14th century. The soft notes of citrus and spice are topped with a light, fluffy head that finishes clean and crisp. This all natural ale is our latest seasonal brew, perfect for lounging lake-side or celebrating at trail\'s end.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4361, 'Belgian-Style Tripel', 'La Fin Du Monde', 9, 1307, 'La Fin du Monde has a brilliant golden color with vigorously effervescent foam.\n\nIt is midly yeasty with a complex palate of malt, fruit and spice notes followed by a smooth, dry finish.', 1);
