INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4374, 'American-Style Lager', 'Lone Star', 4.6500000954, 963, 'The National Beer of Texas\n\nLone Star beer uses the finest hops from the Pacific Northwest with hearty grains from the Central and Northern Plains. The GABF recognized Lone Star as a high quality American Cream Lager by awarding it with a silver medal. Malted barley and corn extract combine to provide Lone Star with nature\'s finest ingredients for brewing. Lone Star\'s ingredients give this beer its full natural flavor. The choicest hops lend complexity and aroma to this beer', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4375, 'Irish-Style Red Ale', 'Thomas Hooker Irish Red Ale', 5.3000001907, 1258, 'Thomas Hooker\'s traditional Irish-Style Red Ale gets its warm, ruby color from an interesting blend of pale, caramel and roasted malts which promote a sweetness that\'s balanced with crisp, authentic, English-style hops. A very unique and drinkable red ale that\'s also favored by amber lovers.\n\nHooker Irish Red is drinks well year-round, not just on St. Paddy\'s day.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4377, 'American-Style Light Lager', 'Bud Dry', 5, 44, 'Introduced in 1989 Bud Dry is the first dry-brewed beer produced by an American brewer. It is brewed with select hops, grains and barley malt, which results in a lighter body and less-sweet taste. As with all Anheuser-Busch beers, it is cold-filtered for a smooth draft taste, but then goes through a second cold-filtered finishing process to provide its unique taste.', 1);
