INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4483, 'Fruit Beer', 'Woodchuck Granny Smith Varietal Draft Cider', 5, 611, 'It is made using 100% Granny Smith apples. This cider has a mouth-watering flavor that is tangy and tart, with just a touch of sweetness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4484, 'Fruit Beer', 'Woodchuck Amber Draft Cider', 5, 611, 'Woodchuck Amber is the original Woodchuck Cider. It\'s made from a blend of apples and fermented with champagne yeast to produce a great tasting and refreshing alcoholic drink. It\'s available in 6-packs and 12-packs, as well as on draft.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4485, 'Belgian-Style Pale Strong Ale', 'Delirium Tremens', 8.5, 285, 'The particular character and the unique taste of Delirium Tremens\" result from the use of three different kinds of yeast. Its very original packing', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4486, 'Belgian-Style Dark Strong Ale', 'Delirium Nocturnum', 9, 285, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4487, 'American-Style Brown Ale', 'Palo Santo Marron', 12, 459, 'Palo Santo Marron (Holy Tree Brown)\n\n\nAn unfiltered, unfettered, unprecedented brown ale aged in handmade wooden brewing vessels. The caramel and vanilla complexity unique to this beer comes from the exotic Paraguayan Palo Santo wood from which these tanks were crafted. Palo Santo means holy tree\" and it\'s wood has been used in South American wine-making communities. ', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4488, 'American-Style Lager', 'Shiner Bock', 4.4000000954, 1179, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4489, 'American-Style Amber/Red Ale', 'Misfit Red', 5.0999999046, 470, 'This medium bodied amber ale has a smooth, malt character with a hint of roasted (dark) malt flavor. This beer has just enough hops to balance and goes well with many foods.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4490, 'American-Style India Pale Ale', 'Hellrazer', 6.1999998093, 470, 'From the first sip this American-style India Pale Ale gets in your face with an unruly hop bitterness, big floral flavors and aroma, and just enough malt character to keep you from giving in and acting up.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4491, 'Porter', 'Bad Moon Porter', 5.0999999046, 470, 'A complex, robust porter, Bad Moon is medium to full bodied with a slightly sweet, malty presence combined with hints of chocolate and coffee. This is a full-bodied beer that gets its chocolate and coffee-like flavors and color from the use of generous amounts of roasted malts. It has a smooth finish and a balanced hop character.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4492, 'German-Style Schwarzbier', 'Blackout', 5, 470, 'Succumb to this dark, medium bodied German-style dark lagerâ€™s smooth roasted flavors and light hoppy bitterness. Donâ€™t worry, youâ€™ll remember everything the next morning.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4494, 'American-Style Pale Ale', 'Venom', 5.5999999046, 470, 'This American-Style Pale Ale is aggressively hopped giving it a citrusy flavor and aroma. No antidote needed. This beer is dark-golden, medium-bodied and very â€œhoppy.â€ It is American-style because American-grown hops are used rather that European.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4495, 'Other Belgian-Style Ales', 'Ozzy', 7.25, 242, 'Brewer\'s Art\'s answer to the Belgian devil\" beers (i.e. Lucifer', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4496, 'Belgian-Style Dubbel', 'Resurrection', 7, 242, 'During the fermentation of the first batch of this Abbey-style dubbel, the yeast died\" and was \"resurrected\" by brewer Chris Cashell. Made with five types of barley malt and lots of sugar', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4498, 'Other Belgian-Style Ales', 'Damnation', 7, 1086, 'Damnation has extraordinary aromas of banana and pear with mouth filling flavors of sweet malt and earthy hops. The lingering finish is dry and slightly bitter but very, very smooth.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4499, 'Imperial or Double India Pale Ale', 'Pliny the Elder', 8, 1086, 'A true leader in the hop-wars of the west coast, Pliny the Elder hits you over the head with hoppy bitterness and manages to smooth the rough edges out enough to become an enjoyable brew.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4500, 'Imperial or Double India Pale Ale', 'Pliny the Younger', 11, 1086, 'Pliny the Younger was Pliny the Elder\'s nephew, in the case of this beer, the Younger\" is a triple IPA. Pliny the Younger is hopped three times more than our standard IPA', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4502, 'Golden or Blonde Ale', 'Aud Blonde', 4.5, 1086, 'Aud Blonde has a refined characteristic, which can be contributed to us blending pilsner malt with a unique hop called crystal. The distinctive characteristic of the crystal hop lends a citrus, almost eucalyptus quality in the aroma and mouth feel of the brew, while leaving the beer with a long dry finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4506, 'American-Style India Pale Ale', 'Thunderhead IPA', 5.8000001907, 1262, 'The Granddaddy of Pale Ales, this style got its start as a special brew for English soldiers living in India.  Our IPA is a medium bodied ale, with a light copper color characterized by an intense hop flavor, with a full flowery hop aroma and higher alcohol content.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4507, 'Fruit Beer', 'Strongbow Cider', 5.3000001907, 320, 'Strongbow is a brand of cider manufactured in England by Bulmers. It is the UK\'s most popular cider[citation needed], accounting for more than half of the draught cider sold in British pubs. Strongbow is also unique in that it is produced with a Royal Warrant.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4508, 'American-Style Cream Ale or Lager', 'Summer Solstice Cerveza Crema', 5.5999999046, 41, 'This copper colored ale is smooth', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4510, 'American-Style Imperial Stout', 'Imperial Eclipse Stout', 9.5, 520, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4511, 'Fruit Beer', 'Blue', 4.9000000954, 1224, 'Sweetwater Blue is a unique light bodied ale enhanced with a hint of fresh blueberries. This euphoric experience begins with an appealing blueberry aroma and finishes as a surprisingly thirst-quenching ale.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4512, 'American-Style Brown Ale', 'Georgia Brown', 5.0999999046, 1224, 'It\'s definitely one of the finer American Brown Ales. A deep, copper colored, mild brown ale.\n\n\nSGB is accentuated by a slight nuttiness from its malt character. Designed to be a session beer it has a real smooth finish with a subtle hop character.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4513, 'Belgian-Style White', 'Hummer', 5.0999999046, 1224, 'SweetWater Hummer is a tasty Belgian White Ale brewed with coriander and orange peel. Cloudy with subtle fruit tones and a lingering finish. Everybody loves a Hummer!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4515, 'Imperial or Double India Pale Ale', 'Maharaja, The', 10.5, 62, 'From Website:\n\nMaharaja is derived from the sanskrit words mahat, - great\" and rajan - \"king\". Much like its namesake', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4516, 'Belgian-Style Quadrupel', 'Reverend, The', 10, 62, 'Belgian-Style Quadrupel Ale', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4518, 'Light American Wheat Ale or Lager', 'Erdinger Weizen', 5.3000001907, 1019, 'The famous Bavarian specialty\n\n\nErdinger Weissbier \'crystal clear\' is particularly welcome as a tasty thirst-quencher for hot summer days.\n\n\nThis specialty wheat beer gets its \'crystal clear\' appearance from a particularly long filtration process, known in the industry as fine filtration\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4520, 'Dark American-Belgo-Style Ale', 'Raison D\'Extra', 20, 459, 'A bigger, bolder version of our Raison D\'Etre.\n\n\nThis is a bulbous, brown ale brewed with a bunch of malt, brown sugar and raisins.\n\n\nIn case you care... the average 12 oz. serving has approximately 425 calories.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4521, 'American-Style India Pale Ale', 'Orlio Seasonal India Pale Ale', 5.4000000954, 955, 'A strong but smooth medium-bodied copper ale with a furity nose, big dry hop flavor, and assertive hop bite that rest upon a subtle but supportive background of complex malt flavors.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4523, 'German-Style Schwarzbier', 'Orlio Seasonal Black Lager', 4.5, 955, 'A rich medium-bodied black lager whose sweet deep malt flavor is balanced by a moderate hop bite and roasted malt tartness that creates notes of bitter chocolate and a slight lingering sweetness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4524, 'Fruit Beer', 'Woodchuck Dark and Dry Draft Cider', 5, 611, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4537, 'Imperial or Double India Pale Ale', 'Double Crooked Tree I.P.A.', 13.600000381, 426, 'From their website:\n\nHave you read the description for the regular Crooked Tree yet? Well this beer is almost the same just double. We actually took the Crooked Tree recipe and doubled all of the ingredients except the water', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4538, 'Imperial or Double India Pale Ale', 'Hopmouth Double IPA', 8, 47, 'The first in our Brew Crew Big Beer Series, Hopmouth Double IPA is a hop loverâ€™s delight!  Deep amber hue with a generous white head, Hopmouth showcases a rich, toasty flavor and a sweet caramel notes as a result of the high-quality Maris Otter Malt, imported from the UK.  The hops are right up front in the aroma, reminiscent of citrus and pine, while the resin-like flavors linger well after the last sip.\n\n\nThis is a BIG beer.  True to our original vision, we have achieved a level of balance and drinkability rarely found in this style.  Delicious on its own, Hopmouth is also a brilliant pair with many boldly-flavored foods, including many cheeses and desserts.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4539, 'American-Style Light Lager', 'Red Dog', 5, 863, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4540, 'American-Style Light Lager', 'Mickey\'s Ice', 5.8000001907, 863, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4541, 'American-Style Light Lager', 'Amstel Light', 3.5, 37, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4544, 'American-Style India Pale Ale', 'Abijah Rowe IPA', 6.8000001907, 1247, 'Traditional English India Pale Ale brewed with all English malt & hopped with a blend of English & American hops. High in strength, bitterness & aroma.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4545, 'German-Style Brown Ale/Altbier', 'Alt', 5.6999998093, 1247, 'Alt, German for old', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4546, 'Extra Special Bitter', 'Old Mill Pond ESB', 6.5, 1247, 'As English style extra special bitter, brewed using the finest malt and hops from England to produce a well balanced medium body pint.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4547, 'American-Style Stout', 'Three Steve Stout', 5.3000001907, 1247, 'Our Stout is brewed with English malt, hops and oats to produce a smooth coffee-like richness with a pleasant bittersweet finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4548, 'American-Style Pale Ale', 'North Pleasant Pale Ale', 5.25, 35, 'Light gold in color, slightly malty and hoppy, with a mild flowery hop finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4549, 'American-Style Brown Ale', 'Massatucky Brown', 5.25, 35, 'Light brown medium bodied ale. Smooth, malty and slightly roasted flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4550, 'American-Style Imperial Stout', 'Two Sisters Imperial Stout', 6.6999998093, 35, 'Dark, rich, hearty, smooth and chocolaty. The two siloettes on the logo are Marina and Kshusha adopted from Russia by Amherst Brewing Company\'s owners, John and Terrie.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4551, 'Herb and Spice Beer', 'Heather Ale', 5.4000000954, 35, '2003 Silver medal winner herb and spice catagory Great American Beer Fest, made without hops, 100% heather flowers giving it a flowery aroma, subtle sweetness, and crisp, clean finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4552, 'German-Style Oktoberfest', 'Lewmeister Oktoberfest', 5.4000000954, 35, 'Amber, full-bodied lager made in the traditional style with German hops. Brewed in early August and put on tap with a keg of the previous years batch by October of each year. This beer is brewed in memory of Lewis\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4553, 'American-Style Brown Ale', 'Raspberry Brown Ale', 4.9000000954, 35, 'Our Massatucky Brown Ale with a subtle raspberry flavoring', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4554, 'South German-Style Hefeweizen', 'Workingman\'s Wheat', 4.9000000954, 35, 'Light crisp American style wheat with a medium hop finish. Made using 25% German wheat malt. Brewed and on tap each summer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4555, 'American-Style Amber/Red Ale', 'Righteous Red', 4.5999999046, 35, 'Reddish amber in color, mild and malty with hop aroma and finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4556, 'American-Style Pale Ale', 'Graduation Ale', 5.5, 35, 'Golden pale ale, slightly hoppy. Brewed each May for the upcoming graduates. It took you four hard years to graduate so we made this one easy to drink. Congratulations!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4557, 'German-Style Pilsener', 'Patriots Pilsner', 4.8000001907, 35, 'A traditional European Pilsner light in body and color. This brew possesses mild hoppy flavors and a crisp clean finish. It is made with traditional European hops: Hallertau and Saaz.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4558, 'Extra Special Bitter', 'Amherst ESB', 5.3499999046, 35, 'Amber in color, medium body with very hoppy aroma and bitter finish. Dry hopped with Oregon Goldings', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4559, 'German-Style Pilsener', 'Honey Pilsner', 5.1999998093, 35, 'Crisp light lager, slightly malty with subtle hints of wildflower honey. Finished with Hallertau and Saaz hops', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4560, 'Porter', 'Puffers Smoked Porter', 4.5999999046, 35, 'Dark, full bodied ale with a prominent smoked malt flavor', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4561, 'Traditional German-Style Bock', 'Boltwood Bock', 5.6999998093, 35, 'Light in color, full bodied and very malty, this lager has a toasted malt flavor. Brewed in February and usually on tap by May with a keg of the previous year\'s batch.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4562, 'Golden or Blonde Ale', 'Bankers Gold', 4.5999999046, 35, 'Just another day at the bank. Very light, mild ale with a subtle hop finish. Brewed once a year in early Spring.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4563, 'Scotch Ale', 'Half in the Bagpipe', 7.1999998093, 35, 'Amber in color, full-bodied strong ale with a prominent malt flavor delicately balanced with Goldings hops. Made with a hint of molasses, this beer is usually on tap for Super Bowl with a keg or two of the previous year\'s batch.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4564, 'American-Style India Pale Ale', 'Cascade IPA', 5.3000001907, 35, 'Light in color, medium body, with a hop bitterness and Cascade hop finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4565, 'German-Style Brown Ale/Altbier', 'Olde #8 Anniversary Ale', 6.8499999046, 35, 'Brewed for August each year to celebrate our Anniversary this strong ales maltiness is balenced by a generous amount of hops', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4566, 'German-Style Pilsener', 'Premium Beer', 4.8000001907, 131, 'Bitburger guarantees premium quality and enjoyment. Drawing on almost 200 years of expertise, the full-bodied, light, Bitburger Premium Beer is of course brewed according to the German Purity Law. Its popular, dry-finished, hoppy taste has secured Bitburger Premium Beer position as Germany\'s no. 1 draught beer.\n\n\n-http://www.bitburger.com/bitburger_beers/bitburger_premium_beer/product_bitburger_premium_beer/index.html', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4567, 'Other Belgian-Style Ales', 'Hell\'s Belle', 7, 113, 'This style originated in Belgium to compete with the pilsners from Czechoslovakia, Germany, and Poland. It is a fruity ale with very strong carbonation, with complex, spicy flavors like cloves.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4568, 'Ordinary Bitter', 'Foster\'s Special Bitter', 5.5, 546, 'http://www.fostersbeer.com/home.aspx?fullbrowser=h', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4570, 'Light American Wheat Ale or Lager', 'Honey Moon Summer Ale', 5.5999999046, 399, 'The Gold Medal-winning Honey Moon Summer Ale is a classic summer ale made even better with real clover honey, fresh orange peel and both pale and white wheat malts.  Proff that brewing the perfect summer ale is a true art.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4571, 'German-Style Pilsener', 'Staropramen', 5, 998, 'Classic famous czech beer from Praha, one of the best beers of the world', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4572, 'American-Style Brown Ale', 'Bad Penny', 5.5, 113, 'This dark brown ale is slightly sweet with a caramel and dark fruit finish. A show case of fine English malts, floor malted the old way. We use pale, chocolate, and crystal malts to produce a complex, but easy drinking dark ale. Very reminescent of northern English ales.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4574, 'American-Style Brown Ale', 'Antler Brown Ale', 4.8000001907, 76, 'This traditional American style brown ale has medium body with a sweet Carastan maltiness. A rich deep chocolate color, our Antler Brown Ale is mildly hopped with Mt. Hood hops. Check out the Antler tap handle... locally grown! This has been our most popular beer over the years... a very easy drinking brew.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4576, 'American-Style Light Lager', 'Cliffhanger Light Ale', 3.2200000286, 76, 'Our lightest brew, this ale has a lower alcohol content than other Barley Creek brews. The Munich malt and Saaz and Hallertau hops give it a distinct German profile. Refreshing with a clean, dry finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4581, 'American-Style Barley Wine Ale', 'Old \'99 Barley Wine', 11, 76, 'First brewed in 1998 to be served on our fifth anniversary (December 15, 1999), this beer just gets better every year. And, every year around our anniversary, we brew a new vintage. With an original gravity of 1099, this Barley Wine is dark and sweet, and is served in a snifter like an after-dinner drink. You\'ll want to savor it slowly.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4582, 'Belgian-Style White', 'Maisel\'s Weisse Kristall', 5.0999999046, 209, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4583, 'Light American Wheat Ale or Lager', 'Schneider Weisse', 5.4000000954, 1023, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4584, 'American-Style Lager', 'Saranac Adirondack Lager', 5.5, 512, 'Our classic German lager has a perfect balance of caramel malt sweetness. Look for a rich, amber color and medium body.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4585, 'Golden or Blonde Ale', 'Organic Ale', 5, 1099, 'A delicately flavored golden ale in which subtle fruity esters from the Samuel Smith yeast strain interact with a background of maltiness and fresh hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4586, 'American-Style Imperial Stout', 'Founders Imperial Stout', 10, 549, 'A fine companion to end a meal or relax in front of the hearth with, this robust ale will cellar well for years. Brewed with ten varieties of malted barley this stout is extremely smooth, complex and rich in body. We recommend serving at cellar temperature. Sit back and enjoy its richness and complexity, your about to drink the ultimate winter warmer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4587, 'American-Style Pale Ale', 'Founders Pale Ale', 5.1999998093, 549, 'A testament to Cascade hops in a bottle. Our medium-bodied, Pale Ale has a distinctive floral hop aroma and refreshing citrus flavor. Founders Pale Ale has a slight malty sweetness with a balanced hop finish. A perfect beer to enjoy while mowing the lawn, boating, grilling with friends or just taking it easy.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4588, 'American Rye Ale or Lager', 'Founders Black Rye', 6.4000000954, 549, 'This beer will surprise you with a big hop flavor and a toasty sweet aroma. Pours black from the chocolate and de-bitterized black rye malts. This ale is medium bodied and is well flavored with hints of toffee, nutty undertones and a dry crisp finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4589, 'American-Style Imperial Stout', 'Founders Breakfast Stout', 8.3000001907, 549, 'You\'ve got to love coffee to truly appreciate this phenomenal brew. Brewed with an abundance of flaked oats, bitter and sweetened imported chocolates, Sumatra and Kona coffee. Breakfast Stout has an intense fresh roasted coffee nose toped with a cinnamon colored frothy head that seems to never fade and makes you wish breakfast could last forever.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4590, 'Fruit Beer', 'Rubaeus', 7, 549, 'As promised Founders Brewing Company has created something unlike the rest. Amongst this summers over croweded race to cloudy wheat beers, we have decided to embark down a path all our own. Using only fresh rasberries, rubaeus translates into intese flavors combined with a no hesitation malt bill. In fermentation we add fresh rasberries at five separate stages to achieve the ulttimate balance between tartness and sweetness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4591, 'Old Ale', 'Curmudgeon', 9.3000001907, 549, 'This Old Ale conjures up thoughts of classic sea fairing ports, there local pubs and the weathered fisherman that frequent them. In traditional style Curmudgeon is brewed with an intense focus on the malt bill creating a very strong, rich, malty characteristic and a sweetness indicative of its cousin the barleywine. We are especially proud of the balance in this beer making it deceptively smooth and drinkable at 9.3% alcohol by volume.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4592, 'American-Style Imperial Stout', 'Founders Kentucky Breakfast', 10, 549, 'A bit of backwoods pleasure without the banjo. This stout is brewed with a hint of coffee and vanilla then aged in oak bourbon barrels. Our process ensures that strong bourbon undertones come through in the finish in every batch we brew. We recommend decanting at room temperature and best enjoyed in a brandy snifter.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4593, 'Imperial or Double India Pale Ale', 'Founders Devil Dancer', 13, 549, 'Founders most complex, most innovative, most feared and yet most revered ale produced. Massive in complexity the huge malt character balances the insane amount of alpha\'s used to create this monster. More IBU\'s than any brewery has documented, more than you would believe and dry-hopped for twenty-six days straight with a combination of 10 hop varieties. Dangerously drinkable and deliciously evil. We dare you to dance with the Devil.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4594, 'American Rye Ale or Lager', 'Founders Red\'s Rye', 6.4000000954, 549, 'Pours a spectacular crimson red with a creamy tan head. Brewed with four varieties of Belgian caramel malts imparting a sweet richness. Red\'s Rye is impressively balanced with its hop bitterness and huge citrus bouquet achieved from the immense amarillo dry hop. The generous amount of malted rye used accentuates a spicy crisp finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4595, 'American-Style Pale Ale', 'Saranac Pale Ale', 5.5, 512, 'A beer that would make the English jealous! This true English Pale Ale is rich and fruity, yet finishes crisp. You\'ll love the copper amber color and medium body.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4596, 'American-Style Brown Ale', 'Saranac Brown Ale', 6, 512, 'This Brown Ale is brewed with American malt and hops, and traditional ale yeast. Look for a sweet, chocolaty taste, with balanced bitterness resulting in a full flavored but smoothly drinkable beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4597, 'American-Style India Pale Ale', 'Saranac India Pale Ale', 5.8000001907, 512, 'A hop lover\'s delight. In the India Pale Ale tradition this brew is very hoppy in both aroma and flavor from the generous amounts of cascade hops used in brewing. Look for a medium to full body and golden straw color.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4598, 'German-Style Schwarzbier', 'Saranac Black Forest', 5.3000001907, 512, 'A Bavarian black beer with distinctive carmel malt sweetness and rich creamy trademark head. Flavorful, yet smooth; it is very drinkable!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4600, 'American-Style Lager', 'Saranac Lager', 4.8000001907, 512, 'A simple well-made Classic American Lager brewed with only choice ingredients. It is extremely mellow refreshingly smooth, glass after glass.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4601, 'Fruit Beer', 'Pomegranate Wheat', 4.6999998093, 512, 'This wholesome combination of unfiltered wheat beer and real pomegranate juice makes a smooth refreshing beer with all the goodness of pomegranate. Look for a light body with a slight tart finish and golden straw color.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4603, 'Belgian-Style White', 'Saranac Belgian White', 5.3000001907, 512, 'Our Belgian Style White beer is smooth, tangy and very thirst quenching. Brewed with a delicate combination of oats, wheat and barley malt, we season it ever so lightly with hops. Then we add sprinklings of coriander and orange peel for a refreshing brew with a citrus aroma. Don\'t be fooled by the cloudy appearance, that\'s expected of a classic Belgian-style Whitbier\" or white beer.\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4604, 'Light American Wheat Ale or Lager', 'Saranac Summer Ale', 4.6999998093, 512, 'ith more than 2,800 bodies of water in the Adirondacks, it\'s not too hard to find a sunny place to relax, rest your feet and look out over the water. To celebrate summer in the Adirondacks, we\'ve brewed a beer with generous amounts of wheat malt for a light, refreshing taste. Look for subtle hints of lemon.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4605, 'German-Style Pilsener', 'Saranac Golden Pilsner', 5.0999999046, 512, 'Saranac Pilsener is a light bodied American Wheat Beer that is judiciously hopped with Cascade and Tettnang hops. Dry-Hopping\" imparts the distinctive hop aroma and finish. Look for a smooth', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4606, 'German-Style Oktoberfest', 'Saranac Octoberfest', 5.4000000954, 512, 'Saranac Octoberfest is a med-bodied, copper colored lager. It\'s rich, malty taste is subtly balanced by Saaz and Tettnang hops. The beer is aged slowly in the tradition of the Octoberfest beers of Munich.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4607, 'Pumpkin Beer', 'Saranac Pumpkin Ale', 5.4000000954, 512, 'Saranac Pumpkin Ale is brewed with Pumpkin, Cinnamon, Allspice, Cloves, Ginger and Vanilla. Look for a full-body and amber color. We\'re sure you\'ll enjoy this special brew!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4608, 'American-Style Lager', 'Saranac Season\'s Best', 5.3000001907, 512, 'Brewed with a special blend of domestic and Belgian malts for a delicate nut-like character, you\'ll love this Lager\'s rich taste and signature hop aroma. The exceptional full-bodied taste reflects our Brewery\'s extraordinary commitment to brewing beers of the highest standard of quality.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4609, 'Porter', 'Saranac Caramel Porter', 5.4000000954, 512, 'Saranac Caramel Porter is a Robust, flavorful porter Reminiscent of a by-gone era. True to brewing tradition, we\'ve used dark caramel malt, as well as Fuggles and East Kent Goldings hops for smooth, yet slightly bitter, roasted flavor, look for hints of the real caramel used in brewing this delicious beer!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4610, 'Traditional German-Style Bock', 'Saranac Black Diamond Bock', 6, 512, 'Saranac Black Diamond Bock is a rich, malty brew, made with traditional German malts and hops and fermented with a lager yeast. Aged for months for a rich but smooth malty taste. A nice reward for enduring your winter months.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4611, 'Oatmeal Stout', 'Saranac Oatmeal Stout', 4.3000001907, 512, 'Saranac Oatmeal Stout is brewed with a delicate balance of the finest grown oats, selected hops, and our traditional ale yeast. Look for a sweet, dark chocolate and roasted taste.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4612, 'Traditional German-Style Bock', 'Roggen Bock', 5.5, 512, 'Brewed in the tradition of great German Bock beers with only German malts and hops, using 20% rye, malt to give it a distinctive spicy, yet smooth character. You\'ll love the rich flavors and deep red color of this unique German Brew.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4614, 'American-Style Brown Ale', 'Saranac Nut Brown Ale', 5.4000000954, 512, 'Saranac Nut Brown Ale\'s soft mellow malt character is rich and smooth with a medium body. Look for hints of toasted malt flavor, slight hop bitterness and amber brown color.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4615, 'American-Style Stout', 'Saranac Mocha Stout', 5, 512, 'Combines the rich roasted flavor of our traditional stout with a deep, robust mocha. Look for a full-bodied taste with a crescendo of complex flavor notes.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4616, 'Scotch Ale', 'Saranac Single Malt', 5.5, 512, 'We use 100% Scottish Maris Otter Malt. Traditionally used in the distilling industry. The combination of the Scottish Malt and slow aging process produces a unique brew as distinctive as single malt whiskey with a flavor than any other beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4617, 'Other Belgian-Style Ales', 'Saranac Belgian Ale', 5.9000000954, 512, 'Saranac Belgian Ale is Deliciously Fruity. It is brewed with a generous amount of Belgian Aromatic Malt, Hand selected hops, and a traditional Belgian ale yeast. Brewed in the Trappist Style\".\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4618, 'Fruit Beer', 'Saranac Mountain Ale', 5, 512, 'This Delicious Fruity ale is brewed with blackberries, raspberries and sweetened with a touch of blueberry honey. Light and refreshing.With a reddish-Amber color, be sure to look for the ale bitter and berry sweetness. Enjoy!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4620, 'Traditional German-Style Bock', 'Saranac Bock', 6, 512, 'Bock is not from the bottom of the barrel! It is a rich malty beer brewed with traditional German ingredients and aged for months to give it a smooth malty character. Look for a rich full-bodied beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4622, 'Extra Special Bitter', 'Saranac Extra Special Bitter', 5.5, 512, 'Bitters are popular pub beers in England and are closely related to the more well known pales ales. Saranac Extra Special Bitter has a rich, malty taste balanced by the pleasant bitterness of Williamette Hops. The finish is dry with a hint of the Foral English Fuggle and Spicy Saaz hop. Enjoy!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4623, 'Imperial or Double India Pale Ale', 'Saranac Imperial IPA', 8.5, 512, 'Saranac Imperial IPA is part our new High Peak Series, a line of beers that are bigger, more complex and flavorful; beers that are meant to be sipped and savored. Saranac Imperial IPA is brewed with 10 different malts and 10 different hops to make a delectably flavorful and complex Imperial IPA - at 85 IBU\'s and 8.5 % alc/vol.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4624, 'American-Style Imperial Stout', 'Saranac Imperial Stout', 9, 512, 'Saranac Imperial Stout is part of our High Peaks Series', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4625, 'Scotch Ale', 'Saranac Scotch Ale', 6, 512, 'This Scotch Ale is a full-bodied, malty sweet ale. True to Scottish brewing tradition, this malty flavor and deep copper brown color are a result of Scottish two row malt and roasted barley.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4626, 'American-Style Amber/Red Ale', 'Stony Face Red Ale', 5.5999999046, 674, 'Hoppyâ€™s gift to those who like their beers Malty. Dark Caramel and Chocolate Malts lend their deep crimson silkiness, and judicious additions of Nugget and Cascade hops give just enough bitterness for balance. Ask your serverâ€™s favorite beer and itâ€™s probably this one.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4627, 'Golden or Blonde Ale', 'Humbles Blond Ale', 3.5, 538, 'Our lightest beer in body and color, Humbles has proven to be our most popular offering.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4628, 'South German-Style Hefeweizen', 'Heat Seeker Hefe', 4, 538, 'A Bavarian style Hefeweizen, a cloudy brew with slight hints of bannana and clove.  Light and refreshing.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4629, 'American-Style Lager', 'Catalina Common Lager', 4, 538, 'This copper colored lager is brewed with hybrid yeast that ferments at warmer temps, giving that clean refreshing taste of a lager with the slight fruitiness of an ale.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4630, 'Ordinary Bitter', 'Spitfire Best Bitter', 4, 538, 'An English style ale, copper in color with subtle English malt hints balanced nicely with fresh Washington grown Yakima Valley hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4631, 'American-Style India Pale Ale', 'Afterburner IPA', 6, 538, 'A hop lover\'s delight!  Our I.P.A. is loaded with Ahtanum, Tohmahawk, Palisade, East Kent, Goldings and finished with the ever popular Cascades, balanced with generous amounts of floor malted crisp Maris Otter barley.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4632, 'Imperial or Double India Pale Ale', 'Hop Henge Imperial IPA', 8.75, 441, 'HOP HENGE IMPERIAL IPA â€“ The Ultimate in Hop Innovation \n\n\nHop Henge Imperial IPA returns to the Bond Street Series line-up this April in extreme fashion. Staying true to the experimental nature of the series and the â€œnever settleâ€ philosophy of Deschutes, our brewers went back to the drawing board to create an amplified version of last yearâ€™s monument to hops.\n\n\nHead Brewer Brett Porter says, â€œThis is a truly exciting and groundbreaking beer. We reformulated everything about the hop recipe to give Hop Henge an extraordinary aroma and flavor similar to a fresh hop beer.â€ In addition to the Cascade and Centennial hops, Deschutes Brewery is experimenting with a hop variety so new that it has yet to be named.\n\n\nThe team here at Bend Public House recommends Hop Henge as the perfect accompaniment for a variety of spicy foods, so be sure to have a bottle handy next time you make a batch of hot wings and go for the five alarm award. The high-octane hoppiness is a wildly refreshing antidote to a wide array of hot foods.\n\n\nLimited Availability April through June.\n\n\nDonâ€™t miss this amazing hop experiment that is sure to leave your taste buds begging for more.\n\n\n-http://www.deschutesbrewery.com/Brews/Bond+Street+Series/default.aspx\n\n\nAvailable April through June.\n\n\nHop Henge I.P.A. was first brewed in 2006 as an agressive West Coast style I.P.A. In 2007, Hop Henge is going Imperial!  Several pounds of  Centennial, Cascade and Northern Brewer hops are in each barrel with a heavy dry-hop presence to top it off.  A blend of crystal, pale and caraston malts creates an overall biscuity characteristic that is dense and muscular, building the alcohol base to support the monstrous hop profile.\n\n\nLike the rest of the Bond Street Series, Hop Henge highlights the creativity and curiosity of our brewers.  Bolder than its English ancestors, with huge hops and a bitter finish, this IPA is no wallflower.\n\n\nAlcohol By Volume: 8.1%\n\n	\n\n\nIBU 95\n\n\nWhen one of our brewers suggested we name our new IPA Hop Henge, he also came up with the idea of actually recreating Stonehenge, only with hop bales.  We were up for the challenge and even though the weather did not want to cooperate, we pulled it off and threw a party afterwards.\n\n	\n\n\n\nphoto: chris mather\n\nRatings, Awards & Notables\n\n\nWorld\'s Best Strong Pale Ale (Imperial IPA)\n\n2007 World Beer Awards\n\n\nWorld\'s 50 Best Beers\n\n2006 International Beer Challenge\n\n\nGold Medal, 92 Points\n\n2006 World Beer Championships\n\n\nSilver Medal, India Pale Ale Category\n\n2006 Australian International Beer Awards\n\n\nModern Brewery Age, February 12, 2007\n\n5 out 5 stars \n\nHop Henge started its life as an India Pale Ale, but this year it was bumped up to â€œImperial IPAâ€ status, with a hefty dose of additional hops.\n\nâ€œThis one is lovely,â€ said taster Tom Conti. â€œThey got it just right.â€\n\nIt pours out a deep amber, with an appealing rocky head, and rich hop aroma wafting from the glass. â€œThey sure dosed it with a lot of hops...[thereâ€™s] a lot of hop bitterness in the taste,â€ one taster observed.\n\nIn addition to the Imperial-level hopping, Hop Henge also boasts Imperial-level alcohol content, with 8.1% a.b.v.\n\nThis was the top-rated beer during its tasting session, and tasters had to dig deep for new superlatives to describe it. â€œThis is a beautiful beer,â€ concluded taster Gregg Glaser. â€œFull of flavor and hops and malt and hops again.â€\n\nâ€œNot for the timid,â€ said taster Robert Lachman.\n\n\n-http://www.deschutesbrewery.com/BrewPub/OnTap/125352.aspx', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4633, 'American-Style Amber/Red Ale', 'First Flight Amber Ale', 4.5, 538, 'Our first beer brewed right here at Flyers.  This ale has brownish red hues with a mild maltiness balanced by an herbal hop character without being bitter.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4634, 'American-Style Brown Ale', 'Barnstormer Brown Ale', 5, 538, 'As the name implies, a rich deep brown ale in color with dark amber highlights.  The flavor is slightly sweet with hints of nuts and toffee finishing smooth with no after taste.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4635, 'Porter', 'Peacemaker Porter', 5.5, 538, 'A robust porter in style, a dark and full bodied ale with hints of roasted barley and flavors of coffee and bitter sweet chocolate on the palate.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4636, 'American-Style Imperial Stout', 'Sick Duck Imperial Stout', 10, 538, 'A rich and very dark Imperial Stout that has a complex flavor profile with hints of molasses and caramel.  Brewed in July to allow time for aging, Sick Duck finishes very smooth for such a big beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4645, 'South German-Style Weizenbock', 'Scratch #7 2008', 7.9000000954, 1286, 'Red and white wheat combined with darker malts add body to this hazy, unfiltered ale.  Fermented with the same yeast as DreamWeaver Wheat replicating the pepper, clove and all-spice flavors found in our year-round beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4646, 'Ordinary Bitter', 'Scratch #11 2008', 4.9000000954, 1286, 'Next up, we present Scratch Beer #11-2008, a Bitter. Back in the day, we used to brew a little number called TrÃ¶egs ESB. Even though this beer was retired almost six years ago, we still get numerous TrÃ¶egs old-schoolers (Martha) begging for its return.\n\n\nWell hang onto your boot straps because Scratch #11 is about as close as we can get to the original ESB without firing up the old brew kettle. The Scratch #11 recipe originates from Chrisâ€™ stint at Englandâ€™s University of Sunderland brewing program. After returning to Colorado the brothers drew on Chrisâ€™ experience and brewed pilot batches tweaking that recipe. The ESB production recipe has direct bloodlines to these pilot batches.\n\n\nThe Thames Valley ale yeast and the roasted barley create a biscuit flavor with nutty undertones. Combining these flavors with the earthiness of East Kent Golding hops creates one hell of a fine session beer. Take in the deep mahogany beauty of this beer and breathe in the full hoppy nose. Itâ€™s not the ESB, but itâ€™s damn special.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4647, 'Belgian-Style Tripel', 'Scratch #10 2008', 10.199999809, 1286, 'he third time just might be the charmâ€”Scratch #10-2008 is our third triple in the Scratch Beer series.\n\n\nThis grand cru has been lovingly aged since late-February, yet still unleashes the slightest hint of alcohol heat.\n\n\nScratch #10-2008 also has a triple flavor inspiration combining the caramel/toffee notes of dememera sugar and molasses, with citrus and licorice flavors created by the orange peel and star anise; lastly we create a peppery/spicy finish with the same trappist yeast has been used in each Scratch Beer Triple.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4648, 'Belgian-Style Tripel', 'Scratch #8 2008', 10.100000381, 1286, 'Revisiting Scratch #3, we take the same Tripel recipe, add beet and cane sugars to the kettle, and run the wort through our hopback brimming with juniper berries, coriander and yarrow flowers.\n\n\nInspired by the herbaceous nose of gin, the berries and flowers give a slight pine/citrus aroma with floral undertones. Scratch #8 has a dry, yeasty finish and noticeable alcohol warming when consumed.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4649, 'American-Style Lager', 'Scratch #6 2007', 5.0999999046, 1286, 'Old world brewing techniques combine with Noble hops for Scratch #6-2007.\n\nThis Export Lager features a single stage decoctionâ€”a first for TrÃ¶egs Brewery. After combining barley and water we transfer a portion of the mash to the brew kettle for a quick boil. The heated mash is then returned to the mash kettle and the brewing process continues. This process deepens the malt character and adds a subtle toffee backbone to this delicious lager.\n\n\nWeâ€™ve added Magnum hops for bittering and Czech Saaz hops for aroma to create a classic European Noble hop flavor. \n\n\nScratch #6-2007 is unfiltered, so there will be a slight haze to the beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4653, 'American-Style Strong Pale Ale', 'Oaked Arrogant Bastard Ale', 7.1999998093, 1204, 'Arrogant Bastard Ale aged in oak barrels.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4654, 'American-Style Lager', 'Hansa Imported Dortmunder', 4.8000001907, 460, 'Surviving beer from the Dortmunder Hansa-Brauerei before being acquired by Actien.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4659, 'Dark American-Belgo-Style Ale', 'Raison D\'Etre', 8, 459, 'A deep, mahogany ale brewed with beet sugar, green raisins, and Belgian-style yeast. As complex as a fine, red wine. Voted American Beer of the Year\" in January 2000 by Malt Advocate Magazine.\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4660, 'Fruit Beer', 'Fort', 18, 459, 'A strong ale brewed with a ridiculous amount of pureed raspberries (over a ton of em!).', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4661, 'American-Style Pale Ale', 'Lawnmower', 4, 459, 'Lawnmower is our intro for those who need a little help jumping feet first into the crazy world that is Dogfish Head beer.\n\n\nIt\'s a starter beer, but it\'s not dumbed down. Lawnmower is made with quality ingredients and is a great thirst quencher - perfect to enjoy after a day in the sun mowing the lawn (or anything else that gets you hot and bothered)!\n\n\nDraft-Only, Limited Distribution', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4663, 'Fruit Beer', 'Black & Blue', 10, 459, 'A belgian-style Strong ale fermented with blackberries and blueberries.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4664, 'Belgian-Style White', 'Red & White', 10, 459, 'A big, belgian-style Wit brewed with coriander and orange peel and fermented with Pinot Noir juice. After fermentation a fraction of the batch is aged in Oregon Pinot Noir barrels, and another fraction is aged on oak staves. The beer is blended together before packaging.\n\n\nThis has been one of our most popular Limited Edition beers at both our Rehoboth Beach, DE brewpub and at festivals. It successfully marries the refreshing citrusy qualities of a Belgian-style white beer with the robust complexity of a bold red wine.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4665, 'Fruit Beer', 'Aprihop', 7, 459, 'Brewed with luscious apricots, finished with whole-leaf hops. A fruit beer for hop heads...\" Ale Street News\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4666, 'American-Style Amber/Red Ale', 'Tap Room No. 21 Amber Ale', 5.6999998093, 1235, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4668, 'American-Style Light Lager', 'Kirin Light', 3.3099999428, 741, 'From Kirin\'s Site:\n\n\nWith just 95 calories and full-flavor, Kirin Light is one of the few great world-class Lights.\n\n\nYou might think Kirin Light would be a lightweight contender, content to let his sturdier siblings steal the show. But this fitness-minded newcomer won\'t compromise. You won\'t have to compromise either - with just 95 calories and full flavor, Kirin Light is one of the few great world-class lights.\n\n\nWhat makes Kirin Light great\n\nCanadian barley malt, Czech hops, rich golden color', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4669, 'Herb and Spice Beer', 'Chateau Jiahu', 9.5, 459, '9000 year old Chateau Jiahu stands apart as the most ancient, chemically-attested alcoholic beverage in the world. Its recreation is based on painstaking excavation by Chinese archaeologists of Jiahuin the Yellow River basin, state-of-the-art microanalysis of pottery residues by American laboratories, and the inspired Neolithic\" brewing of Dogfish Head Craft Brewery. Chateau Jiahu', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4670, 'American-Style Pale Ale', 'Pipe Organ Pale Ale', 4.9000000954, 1248, 'When the British introduced draught pale ale to America, the Pipe Organ Pale Ale is what they had intended. It is truly British in character. Brewed with pale ale malt and a touch of caramel malt, it has a light copper color and subtle body. The maltiness is carefully balanced with only the best English hops - East Kent Goldings. Although this beer has a fair amount of hops, the caramel maltiness perfectly balances its profile.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4672, 'Belgian-Style Tripel', 'Millennium Trippel', 9, 1248, 'A Belgian Trippel is a very special type of beer. A Belgian Trippel could be called the beer version of a Champagne. It is very light in color and highly carbonated giving an appearance similar to Champagne. The flavor is somewhat sweet and malty as well as strong.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4673, 'German-Style Pilsener', 'Celestial Gold', 4.0999999046, 1248, 'This beer is golden in color with a light bubbly effervescence. The light body makes this lager beer appropriate for a business lunch or your evening sipping beer. The light malt flavor also accentuates the hop flavor. From the start, you will notice a fine hop aroma. As you sip the Celestial Gold, the subdued maltiness blends with a slight hop flavor. When this beer finishes, you will notice a pleasant hop bitterness. The bitterness will be mild and quite appropriate. Celestial Gold has been known to induce Celestial bliss.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4674, 'Oatmeal Stout', 'Blast Furnace Oatmeal Stout', 4.8000001907, 1248, 'Stouts have typically been a staple beverage in the UK.  There are many variations of stouts available. You can find dry stouts, milk stouts, Imperial stouts, American stouts, and even stouts made with oats.\n\n\nAt the Church Brew Works we have crafted delicious Toasted Oatmeal Stout.  The oats were blended with the best barley malt we have â€“ and we didnâ€™t stop there.  To give this beer a wonderful roasted flavor, we added unmalted, very darkly roasted barley.  The roasted barley is mixed with a generous portion of chocolate colored malt giving the Blast Furnace Stout a beautiful ruby hue.\n\n\nWe used our finest hops unsparingly to create a delicately balanced bitterness with a rich roastiness and fresh hop aroma. The Fuggles and East Kent Goldings hops were added with heavy hands at two different times.  This unbridled addition grants a full hop flavor to our heavenly drink.\n\n\nA typical oatmeal stout is a bit lower in alcohol than most other stouts as does the Blast Furnace Stout.  Ours, though not typical, does have a welcome medium body.  As you drink this beer, notice the rich, creamy brown head.  Drink it slowly and savor the flavors.  We donâ€™t think you will be disappointed.  Beer is not just for breakfast anymore.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4675, 'German-Style Oktoberfest', 'Church Brew Oktoberfest', 5.1999998093, 1248, 'Our Oktoberfest is a light amber colored beer.  It is malty in both flavor and aroma making it a delicate beer which is very drinkable. The hop bitterness is kept low to accentuate the maltiness.  Notice the crisp clean character and enjoy.  We brewed three batches of this beer so that we can bottle some of it in order to enhance our bottle beer selection.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4676, 'German-Style Heller Bock/Maibock', 'Mad Brewer Maibock', 7, 1248, 'A bock beer is required by German law to start at 17Â°Plato.   For those who are interested, Â°Plato is a measurement of the sugar content before fermentation.  It is a very malty lager beer with medium hop bitterness.  A maibock is a light to amber colored bock beer.  A Maibock is very similar to a Helles/pale bock.  Maibocks were originally brewed for the coming of spring, hence the name Mai (German for May).  The Mad Brewer Maibock is medium amber in color.  Though similar in color to our Pipe Organ Pale Ale, it is very different in character.  Donâ€™t let it get you though, itâ€™s a very strong beer at about 7% alcohol. It has a very clean and malty nose.  The hop bitterness is evident, but not dominant.  The Mad Brewer Maibock will finish quite full and have a delicate sweetness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4677, 'South German-Style Hefeweizen', 'Heavenly Hefeweizen', 4.5, 1248, 'Iâ€™m sure everyone here knows, Pittsburgh has some humid summers.  What can you do to escape the heat and enjoy yourself?  Well Iâ€™ve got the answer.  How about a light thirst quenching Hefe Weizen (pronounced hefa Vite zen) in the cool environment of our Hop Garden patio.  I can hear you wondering, what is a hefe weizen?  Allay your fears my friends because Iâ€™m going to tell you.  A hefe weizen is a special style of beer made with malted wheat and malted barley.  Hefe weizens are from Germany.  This beer is top fermented (an ale) with a very special yeast.  The Hop levels are kept deliberately low in order to bring out the character of the yeast.  If you notice the nose of the beer, it has a clove-like or fruity aroma.  The grain bill includes 50% wheat malt and 50% barley malt.  It is served very carbonated and a touch cloudy.  It is cloudy because it is served unfiltered.  The remaining yeast contributes flavor, adds B vitamins, and improves the beer as it ages.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4678, 'American-Style India Pale Ale', 'Scratch #12 2008', 6.5, 1286, 'In our never-ending pursuit of answering the burning question of whatâ€™s new, we present Scratch #12-2008, an IPA.\n\n \n\nThe IPA is a fickle beast; too much malt and you veer into a high-octane hopbomb, while too little hops can question a beerâ€™s virility. In these chaotic days of shrinking hop harvests, balance is key to a proper IPA. Scratch #12-2008 delivers an intense pine nose married with a subtle wildflower aroma. The Thames Valley yeast adds an earthy nod to traditional English IPAs, while the American hops provide a bold contrasting bitterness.\n\n \n\nBreath deep, relax, enjoy.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4679, 'German-Style Pilsener', 'Jupiler', 5.1999998093, 690, 'Jupiler is the most famous and most popular beer in Belgium. This delicious lager is brewed with the finest ingredients (malt, maize, water, hop, yeast), using undisputed craftsmanship, ensuring an outstanding beer quality. Jupiler offers refreshment on a wide variety of occasions, thanks to its digestibility and accessible taste. Jupiler (5,2 % ABV) is ideally served at a temperature of 3Â°C. The low-alcoholic variant Jupiler N.A. (0.5%) should be served at 1-2Â°C.\n\n\nJupiler has an outspoken image of masculinity, courage and adventure. Furthermore, Jupiler understands men like no other brand and shares their best moments. This combination of male bonding, self-confidence and self-relativation, speaks to all men and makes Jupiler an ally on their road through life.\n\n\nJupiler is the official sponsor of the highest Belgian football division, the Jupiler League, and also supports the Belgian national football team. Just like football, Jupiler is all about competence and ruggedness, effort and effort reward, team spirit and... festivity!', 1);
