INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4724, 'American-Style Barley Wine Ale', 'Druid Fluid', 9.5, 857, 'An epic barleywine, made in the tradition of British Farmhouse Brewing. Lavished with a velvety blend of six different malts this is a warming beer with a lush mouthfeel. The complexities your discriminating palate will detect are ever changing as this barleywine matures. When young, an assertive hop character predominates and with age the beauty of the big malt balance unfolds.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4725, 'Scotch Ale', 'Highlander 80/-', 5, 857, 'A Scottish-style brown ale with an attractive dark cherry color; a soft, lightly chewy body and a hint of licorice in its malt character. Rich and dark yet not too strong. 1996 Best Show at the Chicago Real Ale Festival.', 1);
