INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4727, 'Extra Special Bitter', 'Old Marcus Ale', 6.5999999046, 857, 'Typically Yorkshire. A strong Ale in which all English grown hop varieties are used. The grassy earthy character of Kent Goldings hops is the keynote in the aroma and finish. Try with fish and chips or a meat pie.', 1);
