INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (4992, 'American-Style India Pale Ale', 'High Tide IPA', 6.5, 1009, 'ach and every fall, we experience a coastal experience in San Diego known as the Super High Tide.\" This happens later in the Fall Season when the tidal swings reach a range of about 8 feet in difference between low and high tides. When this happens', 1);
