INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5088, 'American-Style Pale Ale', 'Patchway Pale Ale', 5.5, 833, 'Nicely Hopped, Medium Bodied Pale Ale', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5089, 'American-Style Stout', 'Stone Mason Stout', 6.0999999046, 833, 'Creamy and Dark with a Dry Roasted Finish', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5097, 'Scotch Ale', 'Shepard\'s Crook Scotish Ale', 5.0999999046, 1061, 'Medium in body, brown in color, this ale is delightfully malty with complex caramel overtones lending to the smoothest finish you will ever experience! Donâ€™t be afraid of the dark and try this deceptive delight!!!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5098, 'American-Style Brown Ale', 'LeRoy\'s Brown Ale', 5.3000001907, 1061, 'Brewed in honor of Andrewâ€™s Grandfather LeRoy, this big bad nut brown ale has a fantastic malt character made up of caramel and chocolate flavors. One taste and you will see why this is the baddest beer in the whole damn town!!!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5099, 'American-Style Pale Ale', 'Melting Pot Pale Ale', 5.5, 1061, 'If you liked the Nobleman this one should blow you away! We took malt and hops from around the world to create this International Pale ale! This beer has an incredible amount of hop flavor and the perfect hop bite!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5100, 'American-Style India Pale Ale', 'Old Wylie\'s IPA', 6.1999998093, 1061, 'In honor of our friend and mascot, Wylie the fish, we are hooking you up with an aggressively hopped India Pale, the staple for any fishing trip! This beer is a hop loverâ€™s dream!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5101, 'Special Bitter or Best Bitter', 'Wells Bombardier English Bitter', 5.1999998093, 1348, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5107, 'German-Style Pilsener', 'Baron Pilsner', 4.6999998093, 82, 'Our pilsner is a traditional Northern German Style Pilsner. It has a fantastic malty aroma with a slight spice from the hops. The head is brilliant white and floats on the clean pale lager. The sparkling mouthfeel gives way to a soft malt sweetness that is followed by a long, dry, crisp finish. The balanced clean finish taunts the mouth to take another drink. Lagered for a minimum of 8-12 weeks to ensure smoothness and drinkability.\n\n\nAll ingredients for the beer are imported from Germany. Brewed in accordance to the German Beer Purity Law (Reinheitsgebot) of 1516.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5108, 'German-Style Schwarzbier', 'Baron Schwarzbier', 4.9000000954, 82, 'The Baron Schwarzbier is a classic German black lager, deep black with ruby and brown highlights. At first the Schwarzbier starts with a slight roasty nose and taste, followed by a faint chocolate-malty body with a very crisp, clean, lagered finish. Lagered for an average of 10-12 weeks to provide a very smooth, easy drinking Schwarzbier with full flavor provided by the dark malts. \n\n\nAll ingredients for the beer are imported from Germany. Brewed in accordance to the German Beer Purity Law (Reinheitsgebot) of 1516.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5110, 'German-Style Heller Bock/Maibock', 'Baron Helles Bock', 6.4000000954, 82, 'The Helles-Bock is similar to a traditional Maibock. Bocks are traditionally brewed in the winter / early spring months and are served during the spring / early summer months. The Helles Bock has a copper golden color with a brilliant white head. The body showcases a clean sweet maltiness that is offset by just enough hops to balance it. Very smooth and easy, drinkable yet deceptive at 6.4%.\n\n\nAll ingredients for the beer are imported from Germany. Brewed in accordance to the German Beer Purity Law (Reinheitsgebot) of 1516.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5111, 'German-Style Oktoberfest', 'HofbrÃ¤u Oktoberfestbier', 6.3000001907, 1191, 'HofbrÃ¤u brews a rich, full-bodied beer which goes down ideally with traditional Bavarian cuisine. With its deliciously bitter taste and alcoholic content of 6.3% volume, HofbrÃ¤u Oktoberfestbier is as special as the Beer Festival itself.\n\n\nType: Bottom-fermented, light festive beer', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5112, 'American-Style Amber/Red Ale', 'Red Seal Ale', 5.5, 919, 'Malt and hops are beautifully married in this full-bodied, copper-red Pale Ale. Red Seal is generously hopped for a long, spicy finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5113, 'Belgian-Style Quadrupel', 'Venus - Belgian-style Quadrupel', 16, 858, 'Venusâ€”goddess of love and beautyâ€”exudes grace and voluptuousness. Indulgent with lush dark malts and sensuous star anise, VENUS is decadent and divine in body and spirit. Leisure lounging in French oak Cabernet Sauvignon wine casks results in a lovely liquid feast of fabulously luxurious flavors.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5114, 'Pumpkin Beer', 'Imperial Chocolate Pumpkin Porter', 7.8000001907, 858, 'Intriguing doses of sweet spices, dark cocoa and pumpkin transform an exceptional porter into a\n\nmesmerizing potion. Pour this tantalizing brew into a snifter and seek solace in the depths of its fascinating flavors. Cinnamon, nutmeg and cloves seem familiar but this brew is cloaked in deep, dark secrets that will forever haunt you.\n\n\nThis beer is like nothing you have ever experienced. It is a majestic mÃ©lange of rich, impressive flavors that meld wonderfully with the\n\nwarmth of alcohol. Will it appease or awaken your spirit?', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5115, 'Imperial or Double India Pale Ale', 'Obliteration IV', 8.6999998093, 858, 'With passion and purpose, we present this series of hop-centric beers. Using different hop varieties and brewing techniques, we aim to capture bold, distinct hop characteristics in aroma, flavor and finish. While we explore the\n\nworld of hops, we invite you to learn along with us: these beers offer an incredible opportunity to experience the diversity of hops while engaging the palate and obliterating the senses.\n\n\nObliteration IV is a tremendous Double Wheat beer. Its aroma is pungent with fragrant notes of citrus, spice, pine and alcohol. An even malt-to-wheat ratio provides a sturdy yet satisfying base for showcasing these high alpha-acid hops, creating flavors and textures that entice then intrigue. An American wheat beer yeast allows Obliteration IV to finish with unadulterated hop bitterness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5116, 'Belgian-Style White', 'Samuel Adams Blackberry Witbier', 5.5, 157, 'We brewed this traditional witbier with orange peel and coriander, and then added a hint of blackberry. The flavor is very complex with malt and cereal notes, intense spice and citrus flavors and a smooth, sweet/tart finish. It will be available in January in its own 6-pack and in the Samuel AdamsÂ® Brewmaster\'s Collection Variety 6 & 12-packs.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5117, 'Pumpkin Beer', 'Pumpkinhead Ale', 4.5, 1136, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5118, 'American-Style Imperial Stout', 'Darkness', 10.300000191, 1221, 'This Russian Imperial Stout is a sipper. Problem is that you want to sip it all night. Look for this one to be released when the leaves change color and Halloween is approaching. Brewed with a blend of 8 different malts, oats and candi sugar. This huge stout reflects Surly\'s commitment to brewing intensely flavored beers in small batches. Only 25 barrels of this beer were brewed.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5119, 'Porter', 'Pugsley\'s Signature Series Imperial Porter', 7.0999999046, 1136, 'Imperial Porter is a full bodied, very dark, malty beer with a good roasted character coming from the Crystal, Chocolate and Black Patent Malts used in the mash. Warrior, English Fuggles, and East Kent Goldings Hops balance the malts with a good hop bite. The beer has an OG of 1.070, rounding out after fermentation with just a slight residual sweetness and cutting dry at the finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5120, 'American-Style Barley Wine Ale', 'Pugsley\'s Signature Series Barley Wine Style Ale', 8.5, 1136, 'Barley Wine Style Ale is a big beer made with six different malts (Pale Ale, Crystal, Caramunich, Wheat, Chocolate and Roasted Barley) and balanced with a very full hop charge of Summit, Challenger and Fuggles hops. It is a deep reddish brown color with a complex fruity nose, a very full body, and an interesting balance between grains and hops which ends with a pleasing dry taste. To fully enjoy all the flavours, this ale is best drunk at 55 degrees Fahrenheit.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5122, 'German-Style Schwarzbier', 'B3K Schwarzbier', 5.3000001907, 1379, 'This German-style black lager has a deep color and gently roasted flavors balanced by an understated hoppiness. A thirst-quenching version of dark beer. A 2008 Great American Beer Festival Gold Medal Winner in the German Schwarzbier category.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5123, 'Light American Wheat Ale or Lager', 'Wixa Weiss', 4.8000001907, 1379, 'Anunfiltered wheat beer, weiss beer is a bavarian tradition. Our version is immensely refreshing and authentic, delivering the classic weiss aromas of clove and banana. A two-time Great American Beer Festival Medal winner in the German Style Wheat Ale category.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5125, 'German-Style Pilsener', 'Two Guns Pilsner', 5, 1379, 'A Czech style Pilsner, Two Guns is golden in color with a soft malt character derived from Bohemian barley and just enough spicy bitterness to make another tasted desirable.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5127, 'American-Style Stout', 'Sagebrush Stout', 6.0999999046, 1379, 'A dark, deeply roasted and full-bodied ale. Rich with kisses of chocolate, coffee and oats, it\'s a glorious version of an American-style stout. A longtime house favorite.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5129, 'American-Style Pale Ale', 'Angry Planet Pale Ale', 6.0999999046, 531, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5130, 'Belgian-Style Pale Ale', 'Flat Earth Belgian-style Pale Ale', 5.1999998093, 531, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5131, 'Dark American-Belgo-Style Ale', 'Michelob Dunkel Weisse', 5.5, 44, 'Dunkel Weisse fills a glass like few beers can. The pour releases banana and clove aromas. Roasted malt flavor is complemented by caramel and chocolate undertones. Then a crisp finish refreshes the palate. Undeniable proof that brewing is truly an art.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5133, 'American-Style Amber/Red Ale', 'Calico Amber Ale', 5, 68, 'The Calico Amber Ale was inspired by the beers of England. In England most of the beer is some kind of bitter\".  Bitter is a style that became popular in the late 1800\'s. The most famous examples are brewed in Burton on Trent.  This hard water gives these beer a pleasantly dry bitter finish', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5134, 'Porter', 'Black Marlin Porter', 6, 68, 'Ballast Point Black Marlin Porter is a rich dark chocolaty Porter with a distinctive American hop character.  It is a great beer to go with hearty foods and is surprisingly one of the few beers that goes well with dessert. One of our favorite combinations here at Ballast Point is Black Marlin Porter with apple pie a la mode.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5135, 'Belgian-Style White', 'Wahoo Wheat Beer', 4, 68, 'Ballast Point Wahoo Wheat Beer is created using the same ingredients that were once used in Belgium. Flaked unmalted wheat, oats and malted barley comprise the grain. The unmalted wheat has a high protein content, which causes the beer to be hazy, thus giving it a cloudy, or white\" appearance. A special yeast gives a refreshing tangy flavor that is different from both the sour Weiss beers of Berlin or the banana and clovey Weizens of Bavaria. The mild hopping allows the unique malts to show through and does not conflict with the subtleties of the citrus spicing. This unique citrus character is created by adding a blend of curacao (bitter orange)', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5136, 'Fruit Beer', 'Orange Blossom Pilsner Â²', 11, 954, 'When you make a pot of coffee, but only use half the water, you get some pretty rich coffee. That\'s the approach we took with our latest offering. OBP Squared, with half the water, has 11% alcohol by volume-twice that of original OBP.\n\n\nThe result is a bold, distinct taste of its own. Made with twice as much real orange blossom honey, all-natural ingredients and no refined sugar, it really is the new king bee of beers.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5138, 'Herb and Spice Beer', 'Delta Ale', 7.8000001907, 1352, 'A pitch black stout made with ginger, cinnamon and a touch of molasses. This mediumâ€“bodied ale has flavors of dark chocolate, roasted coffee beans and ginger snap cookies.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5140, 'German-Style Pilsener', 'Little Creatures Pilsner', 4.5999999046, 796, 'Czech Saaz hops are added early in the boil with a late hopping using a hybrid variety we keep to ourselves, giving light flavour and a soft bitterness. Lightly kilned pilsner malt gives the beer both itâ€™s light, slightly golden colour, and leaves a crisp clean taste on the palate.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5142, 'American-Style Lager', 'Phuket Lager', 5, 1287, 'Phuket Beer was created as a refreshing â€˜boutiqueâ€™ beer in view of the exotic island of Phuket with a much lower formula of bitterness than any other domestic rivals in Thailand.\n\n\nPhuket Beer is the first regional beer brewed in Thailand and is positioned to appeal to both the international markets as well as the local population. This is manifested through the label and packaging designs presented in both English and Thai languages.\n\n\nPhuket Beer is well positioned as a premium brand located somewhere between the local standard brands and the imported premium brands. The efforts were inspired from the feeling of being in a tropical paradise. Thai beer consumers have become more sophisticated over the years, by selecting and demanding premium and high-end beers. initial consumer target was Phuket Island\'s significant tourist sector, a sector that is prone to taste and drink locally produced brands.However, strategic positioning and selected marketing of Phuket Beer has resulted in a ray of successful export markets with overwhelming response from the initial markets overseas.\n\n\nThe philosophy is to provide a â€˜tropical lifestyleâ€™ drinking experience in a fresh clean light tasting lager beer with 5% alcohol by volume, which is in total contrast to the other Thai beers that are very bitter with higher alcohol content.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5143, 'Herb and Spice Beer', 'Monteith\'s Summer Ale', 5, 881, 'Monteithâ€™s Summer Ale is a refreshingly different beer. The opportunity to rediscover some flavour highlights of beer styles enjoyed on summer days past, and bring them back to life.\n\n\nAt the end of last century, in England, Europe and the Americas and in New Zealand, there was still an adherence to brew with barley, wholesome grains, hops and other traditional brewing spices.\n\n\nMonteithâ€™s Summer Ale is a bright gold beer with great body from four different malts. Spiced with a single hop variety and a touch of history - a little Rata honey. Subtle, but enough to make all the difference. The result is a truly refreshing herbal spiced beer brewed for the Summer Season â€” a splendid thirst quencher.\n\n\nMonteithâ€™s Summer Ale enjoyed ice-cold with a simple range of fruits, perhaps refreshing wedge of orange or lime. Alternatively, match with summer salads and stir-frys to complement the light flavours.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5144, 'Irish-Style Red Ale', 'Monteith\'s Celtic Beer', 4.4000000954, 881, 'Monteithâ€™s Celtic Beer is considered an â€˜Irish-style aleâ€™ in the heritage of beers of a burnt-red colour traditionally brewed in the Emerald Isle.\n\n\nMonteithâ€™s Celtic Beer has a dry roasted malt flavour characteristic of this style of brewing. This malty characteristic and crisp dryness is derived from malts of the roasted chocolate malt style. The hop character is medium to allow the chocolate malts to show through.\n\n\nMonteithâ€™s brewers have been able to develop traditional ale fermentation characters while allowing the interesting roasted malt notes to come through in the aroma.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5145, 'American-Style Lager', 'Scharer\'s Lager', 5, 1113, 'A traditional German-style lager, fermented at 7-9Â°C and lagered at 4Â°C for 4-6 weeks. The storage produces a soft, natural carbonation. Full-bodied with high bitterness and hop flavour and a dry finish. Hoppy aromatic nose. Available on draught or in 780 mL champagne-style bottles. Can be difficult to acquire.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5146, 'American-Style Pale Ale', 'Epic Pale Ale', 5.4000000954, 506, 'Supreme Champion Beer - New Zealand International Beer Awards', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5147, 'American-Style Pale Ale', 'Seven Gates Pale Ale', 5.5999999046, 1182, 'The story of the Seven Gates comes from rural Pennsylvania, not far from the Spring House Brewery. According to the legend, a mental asylum for the criminally insane stood in the middle of a dense forest. Surrounding the asylum were seven concentric gates, preventing would-be escapees. One terrifying night, the asylum caught fire, test link causing the inmates to flee into the menacing forest. As the tale goes, the former asylum residents were trapped by the seven gates, and eventually succumbed to the perils of the forest. The locals swear that the tormented souls of the inmates will torture anyone brave enough to attempt passage through the Seven Gates of the forest.\n\n\nSpring House Brewing Co. honors the local legend by producing a terrifyingly delicious brew that is as familiar as a friendly forest, yet possesses a mysterious quality that will haunt you from the first taste. The enormous, hopped flavor and the meticulous craftsmanship that goes into each bottle will have you believing that the master brewer must be a direct descendent of the former asylum residents.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5148, 'French & Belgian-Style Saison', 'Two Front Teeth Holiday Ale', 9.3999996185, 1182, 'We welcome the release of â€œTwo Front Teeth Holiday Aleâ€ a Belgian style Saison with subtle real cherry flavoring.  What a good way to drown out in-laws by enjoying a pint or two of this recent release from Spring House Brewing Company.  This is not for the faint palate; this holiday offering is coming in at 9.4% ABV!  We hope you stop by for a taste or a Growler fill but unfortunately you have to stop in for this one.  Enjoy!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5149, 'Porter', 'Baltika 6 Porter', 7, 999, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5151, 'American-Style Lager', 'Estrella Damm', 4.5999999046, 1087, 'Estrella means star in Spanish and as its name suggests the brand has become a star to the people of Barcelona. It has evolved with the city reflecting its character - sophisticated and passionate yet at the same time relaxed and welcoming.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5152, 'American-Style Brown Ale', 'Sprecher Pub Ale', 4.5, 1181, 'Seven varieties of malted barley are combined to give this English-style ale its complex flavor and deep, brown color. A select British yeast culture adds a subtle fruitiness and a blend of choice hops gives this non-bitter ale a soft finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5153, 'American-Style Stout', 'Sprecher Irish Stout', 5.7300000191, 1181, 'This tribute to St. Patrick\'s Day is smooth and creamy, with distinctive flavors and aromas reminiscent of bitter-sweet chocolate and dark coffee. Just one precious batch of this robust, ebony colored stout is brewed each year.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5154, 'Winter Warmer', 'Scratch #16 2008', 6.3000001907, 1286, 'Scratch #16-2008 combines the bitterness of two aroma hops, the lush sweetness of honey and malt and an earthly yeast taste creating a Winter Warmer designed to ward off the coldest days.\n\n\nThe dark, rich amber color comes from a combination of molasses and crystal malts; the Thames Valley brings a distinct earthly flavor; white the blend of hops creates fruity (Amarillo) and piney (Cluster) bitterness.\n\n\nThe Winter Warmer ends with a slight warming sensation from the elevated alcohol content.  Enjoy this one throughout the winter months.  Cheers.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5155, 'German-Style Brown Ale/Altbier', 'Altbier', 5.0999999046, 1170, 'Reddish brown coloring.  Aroma is slightly sweet, much like flavoring.  Very drinkable.  Recommended.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5156, 'Belgian-Style Dubbel', 'Smuttynose Winter Ale', 4.8000001907, 1160, 'Smuttynose Winter Ale is a full-bodied, amber beer brewed with a special Trappist ale yeast. Stylistically reminiscent of a Belgian Abbey Double, it features fruity aromas and flavor, balanced by soft Crystal hops. Warming, mellow & pleasantly complex, Smuttynose Winter Ale is your perfect cold weather companion.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5157, 'American-Style Pale Ale', 'Shoals Pale Ale', 4.6999998093, 1160, 'Our interpretation of a classic English beer style is copper-colored, medium-bodied and highly hopped. Its flavor is delightfully complex: tangy fruit at the start, with an assertive hop crispness and a long malty palate that one well-known beer writer has compared to the flavor of freshly-baked bread.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5158, 'American-Style Brown Ale', 'Old Brown Dog Ale', 5.9000000954, 1160, 'Old Brown Dog has been cited as a classic example of the â€œAmerican Brown Aleâ€ style of beer. Compared to a typical English Brown Ale, Old Brown Dog is fuller-bodied and more strongly hopped.\n\n\nOld Brown Dog has been around for many years. It was first brewed in 1988 at the Northampton Brewery. In 1989 it won a silver medal in its category (American Brown Ale) at the Great American Beer Festival in Denver.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5160, 'American-Style India Pale Ale', 'Smuttynose Finestkind IPA', 5.5999999046, 1160, 'Portsmouth is a colonial era seaport town, so it goes to follow that sooner or later we\'d brew an India Pale Ale as a tribute to those big, hoppy 19th century ales that made the long sea voyage from England\'s temperate shores, \'round the Cape of Good Hope, to the sultry climes of the faraway East Indies.\n\n\nBut there\'s another reason we brewed this beer, one that\'s closer to our home and hearts. Hopheads. \n\n\nTen years ago we brewed our first batch of Shoals Pale Ale, our American interpretation of the traditional British ESB (Extra Special Bitter) style. At the time, it was widely considered to be darned hoppy. However, a funny thing happened over the last decade - our Shoals Pale Ale didn\'t change; beer lovers did, and we started to hear more and more: â€œWhy don\'t you guys make a really hoppy beer?â€\n\n\nYou could say, then, that Smuttynose IPA is a physical salute to the glory of the American hop grower. The citrusy hop flavor coming from a mixture of Simcoe and Santiams is pleasantly balanced by a smooth bitterness from the Amarillo hops. The beer itself is light bodied and crisp with a golden color that will throw a slight haze, as we bottle it unfiltered. At 65 IBU\'s, this is definitely not a training-wheels IPA, but is meant for hop lovers looking to satisfy their craving in a way that\'s not easy to find. We think theyâ€™ll be quite pleased.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5161, 'Porter', 'Robust Porter', 5.4000000954, 1160, 'This hearty, mahogany colored ale is brewed to evoke the dark, full-bodied ales that were a favorite of dockworkers and warehousemen (hence the name â€œPorterâ€) in 19th century London. It is a good bet that when Dickensâ€™ Mr. Pickwick sat down for a pint, we would have been drinking an ale much like our Robust Porter.This is a smooth and very drinkable beer, characterized by its well-balanced malt and hops, plus subtle notes of coffee and chocolate.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5162, 'Other Belgian-Style Ales', 'Uranus - 100% Brettanomyces Golden Ale', 7.1999998093, 858, 'Uranus, ruler of the heavens, reigns supreme with beauty and brilliance. Gloriously gold in color, URANUS Golden Ale holds title to our first 100% Brettanomyces fermentation and conditioning. Brewed by mere mortals and blessed by the beer gods, this ale deserves to be worshipped.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5165, 'Imperial or Double India Pale Ale', 'Ale To The Chief', 8.75, 62, 'Ale to the Chief! We the Brewers of Avery Brewing Company', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5166, 'South German-Style Hefeweizen', 'White Horse Hefeweizen', 4.6999998093, 1081, 'This German inspired wheat ale is packed full of citrus flavor. We use torrified wheat, pale, and carafoam for balance, giving it a nice hazy, straw color and refreshing wheat taste. We ferment the White Horse at higher ale temps with a German style yeast that is known for producing world class wheat beers. We let the yeast take over the majority of the flavoring in our fermenters, providing the beer with banana and clove like esters. We pull some of the yeast with it to give this brew the proper style and to keep its body and character. We than age this brew with zests of citrus and a brewers spice that blends harmoniously with the esters of the yeast and balance out the sweetness of this brew. Truly a favorite in the summer time for breakfast, lunch, or dinner.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5169, 'American-Style Amber/Red Ale', 'Truly Honest Ale', 6, 1081, 'Our signature brew that started it all. This beer is made with four different specialty malts including two foreign caramel malts, a pale malt, and a toasted malt. These malts impart a subtle toasted/sweet taste. This malty brew is than graced with floral and aroma hops which balance the maltiness of this brew and leaves behind a smooth finish on the palate. Top notch ingredients and a truly Honest\" taste makes this a seriously drinkable ale', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5170, 'Pumpkin Beer', 'Ichabod\'s Midnight Ride', 6, 1081, 'This Belgian style pumpkin ale was inspired by the legendary midnight ride of Ichabod Crane. It is a Belgian style ale complimented by a light touch of pumpkin and spice. Fresh toasted pumpkins are added in the mash with the grains adding a touch of the season to the brew. The complex fruitiness and dryness of the yeast is enhanced by the addition of sugars during fermantation. The beer is lightly spiced with pumpkin spice, all spice, and brown sugar. The pale and toasted malts used, provide a pie crust taste and smell in the beer as the higher alcohol\'s produced round out the finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5171, 'Imperial or Double India Pale Ale', 'Daddy Fat Sacs Imperial IPA', 7.5, 1081, 'The one and only IPA that doesn\'t leave your pallet with a bitter after taste. This beer took years of practice to get down and when we finally got it right, we knew it. The beer is made with three malts in considerable additions that leave it with a more malty and sweet taste. There are five different hop additions that were carefully planed out to impart just enough bitterness to give it the body and depth that it needed without leaving you with the astringent and bitter aftertaste. We then added a plethora of English flavor and aroma hops that round out the floral and citrus esters. With the beer being so well balanced between bitter and sweet, we than added big sacks\" of bittering hops to the conditioning vessel to impart all the wonderful citrus and floral notes deep into the beer while leaving the bittering aspects of the hop behind. The result was a beer that makes you want to nose dive right in complimented by the well balanced taste of this wonderful brew.\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5172, 'German-Style Oktoberfest', 'Late Harvest Fest Lager', 6.1999998093, 94, 'This malty, copper-colored lager is styled after Munich\'s traditional Oktoberfest\" beers. A full-bodied', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5173, 'American-Style Pale Ale', 'Liberty Ale', 6, 39, 'San Francisco\'s famouse Liberty Ale was first brewed on the 18th of April, 1975 to celebrate the 200th anniversary of Paul Revere\'s historic ride. It is virtually handmade by the brewers of Anchor Steam Beer in one of the smallest and most traditional breweries in the world. Liberty Ale is made with the finest barley malt, fresh, whole hops, top fermenting yeast, pure water and the simple natural methods which reflect our exceptional respect for the ancient art of brewing. It is dry hopped', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5174, 'Winter Warmer', 'Young\'s Winter Warmer', 5.1999998093, 1348, 'The definitive winter beer and a longtime favourite, Winter Warmer has been making annual appearances on the bar every winter since the 1950s. A rich, ruby-coloured beer, luscious and fullbodied, with a splendid nutty flavour.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5176, 'Belgian-Style Dark Strong Ale', 'embrasse', 9, 428, 'pure-malt dark ale.\n\nrich and intense. good mix between imperial stout (but not as intense) and quadrupel. The beer has real balance and a luxurious mouthfeel', 1);
