INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5187, 'German-Style Pilsener', 'Scratch #17 2009', 5.5999999046, 1286, 'O.G.: 12.7 Plato\n\nA.B.V.: 5.6%\n\nIBU: 43\n\nMalt: Pils\n\nYeast: Augustiner\n\nHops: Select, Tradition, Spalt, Glacier', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5188, 'American Rye Ale or Lager', 'Scratch #18 2009', 7.3000001907, 1286, 'We started with the same grain bill as Scratch #12 (IPA) and substituted 25% rye for the base malt giving Scratch #18 a creamy mouth feel and a full-bodied taste.\n\n\nMount Hood and Chinook hops are combined here for flavoring and aroma, and Warrior hops are used for overall bitterness.\n\n\nThe rye plays off the Mount Hood hops, with both ingredients releasing an herbal, spicy flavor. Cheers.', 1);
