INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5201, 'American-Style India Pale Ale', 'Climax IPA', 6, 384, 'This American-style, hop-driven beer starts with a citrus flavor, followed by two layers of caramel, and finishes on a hoppy note.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5202, 'Extra Special Bitter', 'Climax Extra Special Bitter Ale', 5.5, 384, 'Our E.S.B. is a balanced beer with notes of honey, toffee and caramel, backed by a warm spicy hop bitterness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5203, 'American-Style Brown Ale', 'Climax Nut Brown Ale', 5.1999998093, 384, 'This style of beer is based on the English Mild, which was a combination of a stout and a lighter beer favored by coal miners in Scotland, Ireland and North England.\n\nWe\'ve brought together the grains used to make both a Stout and a Pale Ale and balanced them in our Nut Brown Ale to produce a beer with a chocolate and molasses flavor and hints of caramel and coffee in the background.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5205, 'American-Style Barley Wine Ale', 'Climax Barleywine-Style Ale', 10, 384, 'Brewed for our 10th Anniversary, the Barleywine is a bottle conditioned ale that\'s meant to be laid down and enjoyed for years to come. Up front it has a hop nose and hop flavor on the tongue that fades into malty overtones with flavors of vanilla that go into chocolate. As it has aged, we\'ve noticed flavors of dates and raisins as well.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5206, 'German-Style Heller Bock/Maibock', 'Climax Helles', 4.8000001907, 384, 'The Hoffmann Helles (pronounced Hell-es), which is German for bright\" is a pleasant and easy to drink summer beer with a bready/grainy taste.\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5207, 'German-Style Oktoberfest', 'Climax Oktoberfest', 6.1999998093, 384, 'Oktoberfest is typically available from August to November and has toasty caramel and malty flavor.  It is made from German Noble Hops and massive amounts of MÃ¼nich Malt, which give it an orange color.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5208, 'German-Style Doppelbock', 'Climax Doppel Bock', 8, 384, 'Traditionally brewed by Monks to sustain them while they fasted during lent; the Doppel Bock is a big-bodied and flavorful beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5209, 'Light American Wheat Ale or Lager', 'Climax Wheat Ale', 5, 384, 'Hoffmann Hefeweizen is our newest offering and is a traditional German wheat beer made from 60% wheat malt and 40% barley malt. Wheat Ales such as this typically have banana and clove flavors but as Dave dislikes clove, he used a special yeast to strengthen the banana flavors. Hefeweizen is a nice, light, crisp summer beer that can be served with lemon or flavored syrups.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5210, 'German-Style Pilsener', 'Mama\'s Little Yella Pils', 5.3000001907, 956, 'Mamaâ€™s Little Yella Pils - Our upcoming new canned good is a small-batch version of the beer that made Pilsen, Czechoslovakia famous. Mamaâ€™s is made with hearty amounts of pale malt, German specialty malts, and a blend of traditional (Saaz) and 21st century Bavarian hops. Our first canned lager, itâ€™s also fermented at cool temperatures with a German yeast.\n\n\nThis tasteful reality Czech is the perfect antidote for the watered-down versions of pilsner clogging Americaâ€™s shelves. And Mamaâ€™s gentle hopping (about 35 IBUs) and low ABV (just 5.3%) mean weâ€™re finally honoring consumer requests for a delicious but less-challenging beer. (Hey, we like a good low-dose session beer, too.) Look for our Gold Metal Winner on US shelves in March.\n\n\nSadly, the Feds rejected our â€œTake Two and Call Us in the Morningâ€ line on the can.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5213, 'Imperial or Double India Pale Ale', 'Beyond The Gates Double IPA', 7.5, 1182, 'This beer is for those of you who have been enjoying Seven Gates Pale Ale, and are ready for that next step. Beyond the Gates Double IPA is a highly hopped brew featuring plenty of Cascade and Columbus hops. These varieties give Beyond the Gates a floral citrus aroma, and pack plenty of hopped up flavor into this full-bodied ale, which is coming in @ 7.5 ABV. Let us take you Beyond the Gates, and you\'ll never look back!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5214, 'Porter', 'Baltic Porter', 7.5, 771, 'A traditional lager-style beer, with its dark color and medium body, has a defined hop flavor balanced by the smoothness of extra special roasted malt. This healthy dose of over six malt flavors makes this beer perfect for the transition from winter into spring.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5215, 'Porter', 'Shoo-Fly Porter', 6, 771, 'Lancaster Brewing Companyâ€™s newest release is their limited series of Shoo-Fly Porter. From the heart of Pennsylvania Dutch Country comes a delicious porter made with Lancaster County molasses, eight different malts and grains, and four different styles of hops. This beer has a deep, rich, brown color, medium to full body, and finishes with a smooth mouth feel.', 1);
