INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5220, 'American-Style Pale Ale', 'Rondy Brew 2009', 4.5, 858, 'RondyBrew 2009 is a rendezvous of playful pale malts and feisty American hops, designed to exhilarate your winter-weary soul. This copper-colored ale delivers citrusy and refreshing hop aroma and flavor.\n\n\nThis yearâ€™s label features Rondyâ€™s hottest event: The Running of the Reindeer. If youâ€™re suffering from cabin fever, get out and run with the herd. But ready, set, RUN because this beer is only available for a limited time.\n\n\nRondyBrew adds refreshment and celebration to the festive fare and entertaining events enjoyed during Anchorageâ€™s Fur Rondy Festival.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5221, 'Porter', 'Oak-Aged Imperial Chocolate Pumpkin Porter', 7.8000001907, 858, 'This version is even lovelier than the original. Share with your sweetie.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5223, 'German-Style Doppelbock', 'Smooth Hoperator', 7, 1211, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5224, 'Light American Wheat Ale or Lager', 'Michelob Hop Hound Amber Wheat', 5.3000001907, 44, 'Unfiltered American amber wheat ale brewed with a blend of imported and domestic hops for a balanced slightly citrus hop aroma and caramel malty taste.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5225, 'American-Style Lager', 'Haywords 5000', 7.5, 1089, 'Launched in the year 1983, Haywards 5000 is synonymous with strong beer in India. Haywards 5000 is brewed with the choicest of malts and hops lending itself to a unique flavour profile to suit the Indian taste and preference. Haywards 5000 is the hallmark of original and authentic strong beer which other beer brands aspire for. With such credentials, it is not surprising to see men get together over Haywards 5000. It is the language of friendship amongst men who are proud of their masculinity and look forward to a great time with their friends and peers .\n\n\nhttp://www.sabmiller.in/brands_haywards_5000.html', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5227, 'Other Belgian-Style Ales', 'Richbrau Abbey-Style Ale', 7, 1055, 'This recipe is meant to replicate a Belgian Abbey-style ale. Tart from the Belgian yeast but malty and sweet from the addition of rock candy sugar. Reddish brown in color.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5228, 'Oatmeal Stout', 'Richbrau Oatmeal Stout', 5.1999998093, 1055, 'If you donâ€™t do dark beers, do yourself a favor and cancel that policy for this Oatmeal Stout. Keywords: Sweet Lusciousness; Roasty chocolate. Served on a nitro tap.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5229, 'Belgian-Style White', 'Santa\'s Little Helper Witbier', 5.6999998093, 1055, 'We brewed this pleasantly aromatic wheat beer with the addition of bitter orange peel, coriander and a Belgian Abbey ale yeast strain. This beer is unfiltered and its flavor is tart but sweet and perfumey.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5230, 'American-Style Barley Wine Ale', 'Richbrau Barleywine', 12.300000191, 1055, 'Malty, rich, complex with a notable hops balance. Aged for 90 days. For optimal taste allow the beer to warm on your table or from the heat of your hand around the glass. Â½ pints only. 12.3 abv! Offered downstairs at the Taphouse or Pub Bar/ Dining Room only.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5231, 'American-Style India Pale Ale', 'Richbrau India Pale Ale', 6.1999998093, 1055, 'A hops showcase in aroma, flavor and bitterness. We used the delightful Amarillo hop as the most prominent choice.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5232, 'Porter', 'Big Nasty Porter', 6.4000000954, 1055, 'This dark brown porter has more bark than bite. It is actually fairly sweet and fruity with a nutty/chocolate flavor as well. Highly recommended.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5233, 'American-Style Pale Ale', 'Old Nick Pale Ale', 5.5, 1055, 'Our pale ale is located toward the lighter side of the beer spectrum and we hope it will serve as a stepping stone into the more flavorful world of craft beer. Amber, caramel, fruity, hoppy and bitter.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5234, 'Golden or Blonde Ale', 'Griffin Golden Ale', 5.1999998093, 1055, 'Our lightest and palest signature brew, the Griffin is our house session beer. Notice the spicy character imparted by the German hops we use in the Griffin.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5235, 'Sweet Stout', 'Apache Trout Stout', 5, 876, 'From Arizonaâ€™s high country comes Apache Trout Stout, a robust, full bodied, top fermented ale.  Our brewers use mountain pure water, select roasted barley, hops and yeast to create this traditional style.  Savor the balanced, roasted malt flavor and the lingering sweetness that makes this dry stout a cool weather treat.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5236, 'American-Style Amber/Red Ale', 'Wapiti Amber Ale', 5, 876, 'This is our flagship ale.  The label for this beer is adorned with the majestic Wapiti (aka elk).  Wapiti are abundant in northern Arizona.  They are large and beautiful creatures, which is why we chose this animal to represent this beer.  Wapiti Amber Ale is hand crafted with mountain pure water, two row malted barley, yeast and Yakima Valley hops.  Our brewers use traditional methods to create this full-bodied amber ale with a distinct hoppy aroma.  Wapiti Amber Ale should be enjoyed cool, not cold, to best experience the complex character and flowery aroma.  Serve at 43-47 deg F.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5237, 'American-Style Pale Ale', 'Superstition Pale Ale', 5, 876, 'If you are searching for a light, refreshing pale ale, your search is over!  Superstition Pale Ale is named after the Superstition Mountains which are just east of Phoenix.  This American pale ale is brewed using mountain pure water, select domestic grain, yeast and hops from the Pacific Northwest.  Superstition is copper in color with a clean, malty fullness and finishes with pronounced hop bitterness.  Perfect for enjoying on a warm Arizona day.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5239, 'American-Style India Pale Ale', 'Horny Toad IPA', 7.0999999046, 876, 'If you like big, bold, pronounced hop character, then this beer is for you.  One taste and youâ€™ll find yourself right smack in the middle of Hop Head Heaven!  But this beer has more to offer than just hops.  We use tons of the finest malted barley available to balance out this beer.  The result of our efforts is a beer with a magnitude of hop aroma and bite, yet perfectly balanced with a clean, crisp malty flavor.  WARNING: This beer is not intended for the masses that prefer a mild domestic beer,  Itâ€™s big, bold characteristics and 7.1% a.b.v. are not intended for the weak.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5240, 'French & Belgian-Style Saison', 'Farm Girl Saison', 6, 787, 'This pale golden, Belgian-influenced ale is for everyone: Farm Girl, wannabe Farm Girl. In the Belgian Farmhouse tradition, this brew has a dry malt finish and a spiciness that only Belgian yeasts can create. Smooth and well rounded... this one can please anyone in any situation, whether you are on the water, in the sun, or hiding from winter\'s chill.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5241, 'American-Style Pale Ale', 'Lift Bridge Pale Ale', 5.5, 787, 'Well balanced amber pale ale accentuated by multiple additions of floral Cascade hops and our unique introduction of grapefruit zest to compliment the citrus notes of the hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5242, 'Belgian-Style Dark Strong Ale', 'Biscotti', 7, 787, 'We drew inspiration from Steveâ€™s Grandmotherâ€™s holiday biscotti recipe to create this one of a kind brew. By utilizing an array of malts, oats, wheat, pure local honey, light European hops and delicately spicing with Grains of Paradise, Madagascar vanilla beans, whole star anise, all brought together with traditional Belgian yeast, the result is a cloudy, deep copper colored, complex experience. This delight to the senses should be served in a 12 ounce footed glass and allowed to warm to 50 degrees to fully gather the malt and spice spectrum.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5243, 'American-Style Pale Ale', 'HarvestÃ¶r Fresh Hop Ale', 7.1999998093, 787, 'Constant addition of fresh Cascade and Willamette hops from Bradâ€™s Stillwater hop garden during boil and fermentation process. Aromatic and caramel malts compliment the hop character for an incredibly balanced beer that celebrates the hop harvest without killing your tongue.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5245, 'American-Style Pale Ale', 'Double Mountain Pale Ale', 5.0999999046, 462, 'Itâ€™s easy to forget just how dynamite a well-made pale ale can treat you. Our Pale combines softness from our Pilsner malt,  a touch of sweetness from English crystal malt, and plenty of fruity, resinous Northwest hop flavor. The finish is dry, clean and refreshing.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5246, 'American-Style India Pale Ale', 'India Red Ale (The \\IRA\\\")\"', 6.8000001907, 462, 'The â€œIRAâ€, as itâ€™s known around here, marries a ruby red color and rich body with the hop flavors of an IPA. Our unique ale yeast strain adds a delicious layer of complexity. One of the first beers we made, and an enduring favorite.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5247, 'American-Style India Pale Ale', 'Hop Lava', 7, 462, 'This big, glowing, powerful IPA is packed with assertive Northwest hops that are floral, citrusy and resinous. A healthy dose of Munich malt helps to provide backbone and balance against the hoppy attack. The result? Explosive!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5248, 'German-Style Brown Ale/Altbier', 'Double Mountain Altbier', 5.5999999046, 462, 'This special German-style ale is still made faithfully in Dusseldorf, just down the road from Cologne. Our interpretation is light brown in color and cleanly malty, with a crisp hoppy bitterness in the finish. \n\nBrewed with organic Pilsner and Munich malt; Chocolate and wheat malt, and imported Spalt hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5250, 'Belgian-Style Pale Ale', 'Abbey Extra', 4.5, 1090, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5251, 'Other Belgian-Style Ales', 'Abbey IPA', 7, 1090, 'Belgian IPA, West Coast hops meet belgian yeast. Spicy, fruity and citrusy notes combine for an interesting fusion brew.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5252, 'Extra Special Bitter', 'Barristers Bitter', 4, 1090, 'Low in alcohol but big in flavor. This beer is dry hopped like an IPA but the alcohol and bitterness have been reduced for a great session beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5253, 'Traditional German-Style Bock', 'Sacramento Bock', 6.5, 1090, 'A stronger German lager brewed with lots and lots of Munich malt for a toasty bready like aroma.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5254, 'American-Style Lager', 'Brewhouse Lager', 4.5, 1090, 'A light all malt lager with delicate hop finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5255, 'American-Style Brown Ale', 'Sacramento Nut Brown Ale', 5.4000000954, 1090, 'A malty dark ale wtih ale with a chocolate and biscuit like flavor and balanced finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5256, 'Porter', 'Hellsmoke Porter', 5.6999998093, 1245, 'A roasty robust porter. \n\nBrewed with malt that I smoke over alder and apple wood. \n\n\nGoes great with steak, burgers, \n\nas well as barbecued, grilled, or smoked food. \n\nKielbasi! \n\n\nCheeses? \n\nTry it with Gouda, Brie, Swiss, or Havarti.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5257, 'South German-Style Hefeweizen', 'Wheatley Hills Weizenbock', 7.5, 1245, 'Malty and yeasty, with some sweetness along with hints of cloves and banana. (thanks to the yeast!) \n\n\nLow hop bitterness, highly carbonated. Unfiltered, and so a bit cloudy with that magical yeast. \n\n\nPairs well with Thai, Szechuan, Mexican, and Caribbean -- und Deutsche Nahrung! (German Food!)', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5258, 'American-Style Stout', 'Long Island Potato Stout', 3.9000000954, 1245, 'Brewed with real mashed Long Island Potatoes (or is it Potatos\"?) in the mash - hence', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5259, 'Belgian-Style White', 'Old Walt Smoked Wit', 5, 1245, 'A smoked wit, you say? \n\nYup. \n\n\nResearching smoked beers led me to a world of smoked wheat beers, particularly a smoked wheat beer popular in Poland about 100 years ago. (I\'ll list that one as one of the Blind Bat beers when I\'m happy with the test batches and am ready to offer it to the public.) I didn\'t find anything about smoked wit beers, but decided to experiment a bit (a wit is a wheat beer). I like it, and hope you will too. \n\n\nPictured on the label is Long Island\'s old Walt Whitman, enjoying a smoke and a book in what is purported to be his favorite spot in his native West Hills. Long Island\'s springtime is awakening all around him.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5260, 'South German-Style Hefeweizen', 'Harborfields HefeWeizen', 4.5, 1245, 'Sometimes it\'s good to keep it simple. \n\n\nHarborfields HefeWeizen is modeled after the German classic, but meant to be available fresh (like you would enjoy if you were in Germany). Shipping over long distances is not always a friend to beer. \n\n\nNamed for the Centerport-Greenlawn area of Long Island that is home to the Blind Bat Brewery, Harborfields Hefeweizen goes great with fish, chicken, or a summer salad. Some even like it with brunch! (Please enjoy your waffles responsibly.)', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5261, 'American-Style Imperial Stout', 'Samuel Adams Imperial Stout', 9.1999998093, 157, 'Samuel Adams Imperial Stout is our take on the stouts brewed by 18th century English brewers for the Russian Imperial Court of Catherine II. The special malted barley in this intense and massive brew delivers rich flavours like dark chocolate, coffee and anise.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5262, 'Belgian-Style White', 'Samuel Adams Imperial White', 10.300000191, 157, 'Samuel Adams® Imperial White is a new perspective on the classic witbier style.  Witbiers are normally light and refreshing with a fruity finish and we wanted to see how these characteristics would stand up when we amped up the recipe.  We were totally blown away by the flavors that were created by this beer.\n\n\nThis is not just a more intense version of our spring seasonal Samuel Adams® White Ale.  Imperial White is a new recipe that stands on it own merits.  In fact, it is more of a wine substitute than just another refreshing witbier.  This is a beer that should be sipped and savored and you', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5263, 'German-Style Pilsener', 'HUB Lager', 5.0999999046, 678, 'Our Czech style pilsner is all malt all the time-no choicest rice or corn syrup here! Whole flower Czech Saaz hops balance the delicate honey flavor of our organic Canadian grown pilsner malt creating a golden beer with depth of character.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5264, 'American-Style India Pale Ale', 'Hopworks IPA', 6.5999999046, 678, 'Our namesake IPA is a Northwest classic. Generous additions of Amarillo, Centennial, and Ahtanum hops find their way into the kettle, hop-back, and dry-hop. This judicious use of the brewers spice\" creates rich and resinous flavors of citrus fruit and pine. The finest organic Canadian pilsner malt and organic German Munich and Caramunich malts then bring balance to your new favorite beer.\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5265, 'Extra Special Bitter', 'Velvet ESB', 5.1999998093, 678, 'This English Session Beer has a delicious floral hop aroma and flavor. A heaping helping of organic caramel malt and touch of organic chocolate malt give this beer its beautiful mahogany color while organic oats from Bob\'s Red Mill velvetize the texture. Smoother than an infomertial host at half the price!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5266, 'American-Style Pale Ale', 'Crosstown Pale Ale', 5.3000001907, 678, 'This beer bridges east and west with a lane dedicated to riders who aren\'t afraid to get dirty. Three kinds of organic caramel malts and a 24 pound whirlpool hop bomb keep things interesting while the wheels go round and round. Cheers to narrow knobbies, kazoos, and more cowbell!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5267, 'American-Style Strong Pale Ale', 'Deluxe Organic Ale', 6.9000000954, 678, 'Strong like bull but sweet like your momma. This unique beer is a trifecta of bold flavors. Hops', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5268, 'Oatmeal Stout', 'Survival Stout', 5.3000001907, 678, 'Beer of the Ancients! Barley (Egyptian), Wheat (Mesopotamian), Oats (Egyptian), Amaranth (Aztec), Quinoa (Incan), Spelt (Mesopotamian), and Kamut (Egyptian) sustain the soul with a nutrients cultivated through the millennia. Finished with 15 pounds of cold-pressed Stumptown Hairbender espresso. Unlock the mystery entombed in darkness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5269, 'Imperial or Double India Pale Ale', 'Hop Juice Double IPA', 9.3999996185, 778, 'Here at Left Coast Brewing Co. we pride ourselves on being one of the first breweries to pioneer a Double IPA style beer. In 2003, we brewed our first Double IPA, and haven\'t looked back since. This hop monster uses Premium American 2- Row and a touch of light crystal malt to create a solid malt foundation. The recipe calls for hops to be used in every step of the brewing process; in the mash, in a hop back, in the fermenter, and in the bright tanks. We use hop extract, hop pellets and hop flowers, hence the name Hop Juice. Hop Juice spends more than 4 weeks dry hopping in the fermenter and the bright beer tank. It is approximately 9.4% abv and has massive IBUs. Hop usage is over 4lbs per barrel. Hopeheads, step up to the plate!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5270, 'American-Style India Pale Ale', 'Renegade IPA', 5.8000001907, 509, 'Our award-winning India Pale Ale  is a hop loverâ€™s dream. We use Galena and Cascade hops in the kettle and dry-hop with more cascade during conditioning. This copper-colored ale has a nice maltiness to balance all the hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5271, 'Fruit Beer', 'Long\'s Peak Raspberry Wheat', 4.1999998093, 509, 'This is one of our most popular beers. It is a light and refreshing raspberry-flavored wheat ale.  We use pure raspberry extract to give this beer its fruity aroma and flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5272, 'Light American Wheat Ale or Lager', 'Stinger Wild Honey Wheat', 6, 509, 'We put close to a half a pound of Colorado honey per case in this wheat ale. The honey adds a sweetness and raises the alcohol content slightly. We use Mount hood and hallertau hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5273, 'Porter', 'Estes Park Porter', 4.5999999046, 509, 'Porters are dark ales without the roasted malt character of a stout. Our porter is a brown porter with a very smooth, balanced profile.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5274, 'Golden or Blonde Ale', 'Estes Park Gold', 4.1999998093, 509, 'This is our smooth golden ale. Our Gold has a medium body and low to medium hop bitterness \n\nand aroma.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5275, 'American-Style Stout', 'Samson Stout', 5, 509, 'Our commemorative ale honors Samson the elk. This is an oatmeal stout that is full-bodied with a wonderful roasted flavor and just a touch of sweetness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5276, 'American-Style Amber/Red Ale', 'Trail Ridge Red', 4.5, 509, 'Our red is a special bitter. This means it has medium body and mild bitterness. These balance well with the rich copper color. We use American hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5277, 'Golden or Blonde Ale', 'Border Gold Oranic Ale', 6, 260, 'According to legend, gold panned from the crystal clear Border streams of the Yarrow valley, nearby the site of the Broughton Ales brewery, was used to make the third wedding ring of Mary Queen of Scots.\n\n \n\nThis golden coloured ale is brewed with a skilful blend of organically grown hops and malt, and pure Scottish water to the exacting standards required by Soil Association Certification Ltd. of the United Kingdom.\n\n\nThe end result is a light golden coloured, clean tasting beer with an excellent hop aroma and aftertaste.\n\n\nAt 6.0% ABV a premium ale for those who know and care about what they eat and drink.\n\n\nBorder Gold has won several awards, the most recent being a bronze medal in the International Brewing Awards in Munich 2005.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5278, 'American-Style Barley Wine Ale', 'Flying Mouflan', 9.3000001907, 1286, 'This strong alluring ale is two beers in one. Cracking open a fresh bottle unleashes hops and heat with more than 100 IBUs emanating from three hop varieties and sweet burn of 9.3% ABV. Cellaring the Flying Mouflan in a cool dark place at 50 degrees for a minimum of four months will mellow out the hops and wash away the heat. If you can resist temptation you will be rewarded with two memorable beers in a single bottle.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5283, 'American-Style India Pale Ale', 'Liberation Ale', 6, 797, 'With about 6.0% ABV, this one will surely warm the ol\' gullet during a cold central Texas winter - both days! Our India Pale Ale (IPA) is light in color and strong in hop character. Dry hopping with Cascade hops, gives this winter favorite a delicious citrus note reminiscent of a Texas Ruby Red. Yes, it\'s warming but you don\'t need to be huddled around the furnace to enjoy this one! IPA fans really love this ale during a beautiful Texas winter on the porch or indoors. Available December - April', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5284, 'American-Style Imperial Stout', 'Sexual Chocolate', 10, 543, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5285, 'American-Style Strong Pale Ale', 'Bell\'s Batch 9000', 12.5, 100, 'Black', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5286, 'Imperial or Double India Pale Ale', 'Uberhoppy Imperial IPA', 10.5, 1320, 'A massively hopped Imperial IPA brewed to celebrate Valley Brews 10th anniversary. 1 pound of hops were added every 10 minutes during a 100 minute boil, a total of 10 different boiling hops. 4 different fresh hops were used in the hopback and then the beer was passed through a chamber containing 10 lbs of fresh hops on the way from the serving tanks to the bar taps. Hopperiffic.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5287, 'American-Style Stout', 'Black Cat Stout', 6, 1320, 'Our Black Cat Stout is brewed using a blend of Chocolate, Carafa, Crystal and Munich malts. A small amount of wheat and oatmeal are added to give the beer body and mouth feel. Small amounts of roasted malts are added to give the beer a dry \n\ncoffee finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5288, 'American-Style Pale Ale', 'Hitman Gold', 5.5, 1320, 'Hitman Gold is an American Pale Ale. The flavor is smooth but aggressively hopped with Simcoe Hops. The beer is best described as a â€œmini-IPAâ€. The beer is inspired by the famous wrestler Bret The Hitman Hart, who also is the artist who designed and drew the logo.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5289, 'English-Style Pale Mild Ale', 'Valley Brewing London Tavern Ale', 4.4000000954, 1320, 'A classic English Style Mild with a light hop profile. London Tavern Ale is a well balanced beer with caramel flavor and English character. Only traditional English Kent Goldings and Fuggles hops are used.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5290, 'American-Style Amber/Red Ale', 'Indian Red Ale', 6.5, 1320, 'A flavorful Red Ale with a generous helping of fresh Cascade hops. A full-bodied unfiltered beer with a slightly dry and roasted finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5291, 'Imperial or Double India Pale Ale', 'Cobra-Hood IPA', 7.3000001907, 1320, 'Our India Pale Ale is brewed in a California IPA style. The beer is generously hopped for bitterness with Magnum hops and finished with fresh Amarillo, Ahtanum and Cascade hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5292, 'Light American Wheat Ale or Lager', 'Valley Brew Pale Wheat', 4, 1320, 'An American style Wheat Ale made with 50% malted 2 Row barley and 50% malted wheat. Valley Brew Pale Wheat is lightly hopped to produce a smooth and satisfying finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5293, 'American-Style Pale Ale', 'Ports Pale Ale', 4.3000001907, 1320, 'Ports Pale Ale is an extremely light and easy to drink Pale Ale. It is brewed with the finest German and English Malt and Hops. Brewed for the local Stockton ports baseball team.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5294, 'Fruit Beer', 'Valley Brewing Apricot Ale', 4, 1320, 'Apricot Ale is a fruit beer produced using 100% Apricot fruit extract. The beer has a hint of Apricot in the nose and a sweet clean and refreshing finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5295, 'Fruit Beer', 'Valley Berry Wheat', 4, 1320, 'Valley Berry Wheat is a fruit beer produced using pure fruit extracts of Raspberry, Wildberry, Cherry and Blueberry. The beer has a wonderful fruity nose and a smooth clean finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5296, 'Golden or Blonde Ale', 'Fat City Ale', 4.5, 1320, 'Fat City Ale is the newest release from Valley Brewing Company. Fat City Ale is produced using only the finest 2-Row Barley and fresh Hops resulting in a crisp and refreshing flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5297, 'American-Style Brown Ale', 'Thunder Hole Ale', 4.8000001907, 72, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5298, 'Imperial or Double India Pale Ale', 'Double Wide I.P.A.', 8.5, 161, 'Double-Wide I.P.A. uses Zeus and Magnum hops for both bittering and aroma, with Ahtanum hops added solely for aroma. Two additional dry hopping regimens employ Ahtanum, Centennial, and Chinook varietals. The resulting beer â€“ not surprisingly â€“ has a hop forward aroma, redolent of peach and apricot. The assertive flavor bursts forth with citrus notes of blood orange and lemon, a caramel malt backbone serves to balance the intensity of the hops. There is little restraint in the flavor of this beer; it is certainly not for the pedestrian palate.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5299, 'American-Style India Pale Ale', 'Single-Wide I.P.A.', 5.6999998093, 161, 'The latest addition to the Boulevard family of year-around beers, Single-Wide I.P.A. is our take on a style that originated in 18th century Great Britain. This American version -- inspired by our Smokestack Series Double-Wide I.P.A. -- boasts a heady combination of six varieties of hops, some of which were employed for dry-hopping.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5300, 'Irish-Style Red Ale', 'Boulevard Irish Ale', 5.8000001907, 161, 'Irish Ale, Boulevardâ€™s spring seasonal beer, is our Midwestern tribute to the legendary red ales of old Ireland. Our recipe combines six kinds of pale and roasted barley malts to provide a rich, toasty flavor and tawny reddish hue.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5301, 'German-Style Heller Bock/Maibock', 'Boulevard Maibock', 5.9499998093, 161, 'First introduced in 2008, Boulevard Maibock is a new, limited-release seasonal brew. A welcome sign of Spring, this beer is a traditional German lager, lighter in color than other bocks. Boulevardâ€™s Maibock is a distinctive, refreshing take on this springtime classic.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5302, 'Belgian-Style White', 'Bouldevard ZÅŒN', 4.4000000954, 161, 'Boulevardâ€™s summer seasonal is our interpretation of a classic Belgian witbier. ZÅŒN (Flemish for â€œsunâ€) combines the subtle flavors of coriander and orange peel with other traditional ingredients to create a delightful, refreshing summertime brew. Available from May through August, in bottles and draught.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5303, 'German-Style Oktoberfest', 'Bob\'s \'47 Oktoberfest', 5.8000001907, 161, 'Boulevardâ€™s fall seasonal beer, Bobâ€™s â€™47 Oktoberfest is a medium-bodied, dark amber brew with a malty flavor and well-balanced hop character. With this Munich-style lager we solute our friend Bob Werkowitch, Master Brewer and 1947 graduate of the U.S. Brewerâ€˜s Academy. Available from September through October in bottles and on draught.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5304, 'Imperial or Double India Pale Ale', 'Hoppe Imperial Extra Pale Ale', 8, 1173, 'The simplicity of Hoppe tests the skill and ability of the brewer to create something truly majestic. We craft this much like a sculptor who uses only a hammer and chisel to shape stone into a masterpiece. Hoppe is spawned of these few essentials: barley, wheat, hops, yeast and water. This limited palette is an exercise in minimalism, with refined elements which are deliberately selected. This simple combination creates a golden shimmering brew infused with delicate aromas. The artful nature of this beer is exposed with the first taste. As the malt and hops create a composition of flavors, an elegant finish leaves an impression that your tastes will not soon forget.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5305, 'Imperial or Double India Pale Ale', 'Gemini Imperial Blended Ale', 10.5, 1173, 'High in the winter sky, two parallel stick figures are visible & known as â€œthe twins,â€ or the constellation Gemini. The astronauts of the 1960s flew as teams of two in a program named after the celestial pairing. At Southern Tier, we have our own fraternal twins, Hoppe & Unearthly. Blended together & placed in this vessel, the mission of our Gemini is to travel high & take passengers on a journey far into the heavens.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5306, 'American-Style India Pale Ale', 'Victorian IPA', 6.1999998093, 1166, 'Our IPA has lots of malt and hop character.  This style originated during Queen Victoriaâ€™s reign as a strong, flavorful ale, which was able to survive the ocean voyage from England to India during the British occupation.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5307, 'American-Style Pale Ale', 'Burning Bird Pale Ale', 4.8000001907, 1166, 'The Burning Bird is our representation of the Phoenix, our cityâ€™s namesake, a mythical bird which lived 500 years, consumed itself by fire, then was reborn from its ashes. Like the phoenix of old, this pale ale is reborn from the time when fabulous hoppy brews were more than mere myths.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5308, 'German-Style Pilsener', 'Lionshead', 4.5, 792, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5310, 'Other Belgian-Style Ales', 'Dubble Fantasy', 5, 1100, 'Abbey ale with hints of bubble gum flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5311, 'German-Style Pilsener', 'Flensburger Pilsner', 4.8000001907, 534, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5312, 'German-Style Heller Bock/Maibock', 'Dundee Pale Bock Lager', 6.25, 656, 'Certain things seem interminable. The line at the DMV. The commencement address. Winter. \n\n\nBut take heart. Even the longest, darkest winter gives way to spring and a new sense of optimism that the best is just ahead. That thoughtâ€”and an itchy wool robeâ€”sustained the monks through times of fasting. Their reward? A bock beer with the power to rejuvenate the body and the soul. That, and the opportunity to exchange the wool robe for a nice burlap one. \n\n\nSo when youâ€™re stuck in the middle of that seemingly endless glass of mediocre beer, be optimistic. A Dundee Pale Bock awaits.\n\n\nA traditional German Maibock with a deep, golden color, malty-sweet aroma, and clean finish from Magnum and Czech Saaz hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5313, 'Porter', 'Dundee Porter', 6.5, 656, 'More and more weâ€™re becoming a nation of milquetoasts. And nowhere is that more evident than in our beerâ€”puny liquids without calories, carbs, or cojones. \n\n\nBut it neednâ€™t be that way, my friend! Sure, we donâ€™t want to make a living shoveling coal or washing clothes on a rock. But we can recapture the more robust spirit of a time gone by.\n\n\nDundee Porter is a throwback to a different eraâ€”when times were tough and so were the people. They drank big beers, slapped each other on the back, and toasted the good lifeâ€”even though they only saw it from a distance. \n\n\nWith Dundee Porter, you can get a taste of it.\n\n\nBig and malty flavor from aromatic dark-roasted grains balanced by a subtle hint of hops. Deep color and rich caramel flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5314, 'Light American Wheat Ale or Lager', 'Dundee Wheat Beer', 4.1999998093, 656, 'Senator Joseph McCarthy. The Hollow Earth Society. Members of the Spanish Inquisition. All convinced they were blessed with the gift of clarity. And proof that clarity might be overrated. \n\n\nSometimes, being unclearâ€”and a little flexibleâ€”is a good thing. Like when you are looking for the perfect refreshment. Dundee Wheat Beer combines wheat and yeast into a refreshing hefeweizen-style brew. Unfiltered, so it is slightly cloudy and pleasantly unclear. \n\n\nSo the next time youâ€™re convinced youâ€™re right about what youâ€™ve been drinking, give a little â€œunclarityâ€ a try.\n\n\nAn American-style, unfiltered wheat beer. Pale straw in color with a light cloudiness from carefully selected wheat and yeast. Pleasant spicy malt aroma and a crisp hop finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5315, 'American-Style Pale Ale', 'Dundee Pale Ale', 5.3000001907, 656, 'Behold the hop. A flower, but too unsightly to be used for decoration. The ugly duckling of the flower family. \n\n\nOh, but the secret it holds. Soak it in some water with the right companions and it surrenders its bouquet to use a corny flower metaphor. That unique hop flavor is the difference between a fine pale ale and more ordinary beers. \n\n\nDundee Pale Ale understands the secret of the hop flowerâ€”the power to turn an ordinary beer into something extraordinary. \n\n\nAnd the power to make you indescribably hoppy. \n\n\nA wonderfully balanced true pale ale. Cascade, Tomahawk, and Amarillo hops create a citrusy aroma with a smooth, crisp finish. A subdued malt character nicely complements the hop complexity and produces an orangey amber hue.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5316, 'American-Style India Pale Ale', 'Dundee India Pale Ale', 6.3000001907, 656, 'It takes a bit of seasoning to understand that bitter can be a good thing. \n\n\nWhen youâ€™re a child, you want everything to be sweet. When youâ€™re a teenager, you want everything to be sweeeeet. But when you get a bit wiser and acquire some taste, you realize that bitter is an excellent alternative. Itâ€™s a sign of complexity. Itâ€™s a sign of maturity. Itâ€™s a sign of the times. Face it, modern life demands a little bitterness every once in a while. \n\n\nWhen you are in the mood for something bitter, Dundee IPA is hop bitterness at its finest. As the youngsters would say, thatâ€™s sweet.\n\n\nAn aggressively hopped IPA brewed with Amarillo and Simcoe hops. Blended Crystal malts balance the flavors with a distinctive spicy aroma and a long, crisp finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5317, 'Winter Warmer', 'Dundee Festive Ale', 6.1999998093, 656, 'So did you ever ask yourself...what exactly is there to be festive about during the holidays? Your brother in-law snoring on the air mattress? (Boy, letâ€™s hope thatâ€™s snoring.) The variety of toys with realistic sound effects? Fruit cake?\n\n\nDundee Festive Ale may be one of the few exceptions. One sip of the carefully matched seasonal spices and you realize that the holidays are worth paying...waiting for.\n\n\nOh, there may be one other thing to be festive about. Thereâ€™s a 13 to 1 chance against your brother in-lawâ€™s plane leaving on time. But at least thereâ€™s a chance.\n\n\nFestively hearty and full bodied with a mild nutmeg, allspice, cinnamon, and orange peel spice character and subtle crystal malt flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5319, 'American-Style Lager', 'Flywheel Bright Lager', 5, 851, 'The kinetic beauty of spicy hops grabs you by the nose and lets you know: this is German-inspired beer. A mild, bready malt sweetness greets you at the lips, smoothing the crisp hop flavors. Flywheel is meant for bombastic celebrations of singing voices and clamoring mugs. But then, that first contented moment of happy hour is uniquely celebratory as well.\n\n\nFlywheel Bright Lager goes great with hot dogs, guitar jams, stir fry, bowling, taco salad, house warmings, baked potatoes, Saturday morning cartoons, tequila, gouda cheese, karaoke, bagel & schmear, Euchre, fresh berries, asparagus, 16-inch softball, sushi, darts, Pad Thai, family reunions, pb&j, flan, romantic weekend getaways, garlic bread, pay raises, nachos, baby showers, fondue, and Frisbee golf.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5320, 'German-Style Pilsener', 'CD-Pils', 4.9000000954, 451, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5321, 'American-Style Amber/Red Ale', 'Saint Arnold Amber Ale', 5.5, 1091, 'A well balanced, full flavored, amber ale. It has a rich, malty body with a pleasant caramel character derived from a specialty Caravienne malt. A complex hop aroma, with a hint of floral and citrus comes from a combination of Cascades and Liberty hops. It has a rich, creamy head with a fine lace. The light fruitiness, characteristic of ales, is derived from a proprietary yeast strain. \n\n\nSaint Arnold Amber Ale is best consumed at 50 - 55Â° Fahrenheit.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5322, 'American-Style India Pale Ale', 'Elissa IPA', 6.5999999046, 1091, 'A traditional India Pale Ale, the Elissa IPA is very hoppy with a properly balanced malty body. Elissa has huge hop additions in the kettle that give it a wonderful bitterness and is then dry-hopped in the fermenter to create the pleasant floral, hoppy nose. Our reverse osmosis water makes the bitter very soft with no harsh notes to it. The maltiness is derived from British Maris Otter malt. Its rich flavor stands up to the hops that would otherwise dominate this beer. The Elissa is an authentic version of an India Pale Ale (IPA) style.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5323, 'Traditional German-Style Bock', 'Saint Arnold Spring Bock', 6.4000000954, 1091, 'An authentic, German-style Bock, celebrating the coming of spring. This strong, deeply flavored lager has been aged to create a smooth, malty taste with a hint of sweetness. A light addition of German hops balances the malt flavor. \n\n\nSaint Arnold Spring Bock is best consumed at 40Â° Fahrenheit.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5324, 'Old Ale', 'Jasparilla', 9, 692, 'Brilliantly golden, Jasperilla is a unique take on an old ale. Biscuity malt flavors meld with subtle plum and berry notes, produced by a special blend of English Old Ale and Chico yeasts. Brewed once a year, and aged for six months prior to release, the Jasperilla is smooth despite its 9% abv.\n\nJasper dressed as Hosehead from Strange Brew\n\n\nWe named this beer after our dog Jasper because he has brought so much joy to our lives. We got Jasper from a local dog rescue group called Mixed Breed Rescue. He has been a constant source of smiles and kept our spirits high through many late nights and long hours at the brewery.\n\n\nJasperilla is so good and smooth that you\'ll beg like a dog for more, roll over for a belly rub, howl at the moon...you get the picture.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5325, 'German-Style Pilsener', 'Jever Pilsener', 4.9000000954, 714, 'A Norther German (Friesian) Pilsener that is cattegoristic of the style. It is a little more hoppy than Czech pilseners giving it a more herb (bitter?) flavor.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5326, 'German-Style Pilsener', 'WernesgrÃ¼ner Pils Legende', 4.9000000954, 1350, 'A classic German-style Pils', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5328, 'Belgian-Style Pale Strong Ale', 'Gran Met', 10.5, 1330, 'Our Gran Met (Grand Master) is an Ale made from our Propriatery yeast from a small brewery in Belgium. We use Cane and Beet sugar, along with German Pilsner Malt to create the unique flavor of this age old style. We also use a unique fermentation process of slowly adding the sugars to the fermentation during fermentation. This allows the yeast to more slowly ferment the beer without overloading the yeast. This process allows for a more clean, tight flavor profile.10% alc by vol. \n\n\nBottle Conditioned and Refermented.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5329, 'Light American Wheat Ale or Lager', 'White Magick of the Sun', 6.75, 1330, 'This Wheat ale is made with a little Voodoo Twist. We use our house Belgian Ale yeast and add the average unmalted wheat, wheat, barley,raw oats, malted rye and just add more of each. Hopped evenly and spiced with coriander, bitter and sweet orange peel, juniper berries, 12 varieties of peppercorns,lemon grass and caraway. About 6.75% alc by vol. \n\n\nBottle Conditioned and Refermented.  NOT A WIT BIER!!! Just stuff we like in beer that goes great with food. \n\n\nThis beer is to be the answer to those hot summer days. Blows away carbonated water!!!!!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5330, 'Belgian-Style Tripel', 'Voodoo Love Child', 10.5, 1330, 'This is our Gran Met Ale aged on intimate Passion Fruit, Raspberry and Michigan Tart Cherry. Lightly spiced with passion. Light Reddish hue and with a alcohol strength of 10% and nicely carbonated in the bottle. \n\n\nAgain Bottle Conditioned and Fermented.  \n\n\nEnjoy a couple and have your own Voodoo Love Child later!!!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5331, 'American-Style Imperial Stout', 'Black Magick', 15, 1330, 'This is an Imperial Stout that is Brewed to great strength and complexity. We then gently age that beer in Elaigh Craig 13 1/2 year old Bourbon Barrels for 1 yr. and then primed and bottled to bottle condition for continued aging and celler life. Black Magick should be able to be aged up to about 5 years, to heighten the complexity and smooth nature of this beer. This beer should be opened to breath, pour into snifter and enjoy at 55-60 F. \n\n\nThis is a truly Barrel aged ale and is not to have a large carbonation. We bottle condition it to achieve cask like characteristics.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5332, 'American-Style India Pale Ale', '4 Seasons IPA', 8, 1330, 'India Pale Ale is a wonderful example of marriage.  Bitter Hops and Boasting Barley come together to make one of the most sought after ales of the Craft Beer masses. Bottle Conditioned and Refermented.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5333, 'American-Style Imperial Stout', 'Big Black Voodoo Daddy', 12, 1330, 'This is our Imperial Stout that we age in conditioning tanks with oak staves.  This adds a classic character of old world aging with out the Bourbon tones. This black ale is about 12% alc. and very rich in roasted and chocolate malt tones.  Velvety smooth and able tobe aged for years. For now this will be a Seasonal Beer Offering. \n\n\nAll Beers to be bottle conditioned and Refermented.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5335, 'American-Style Brown Ale', 'Wynona\'s Big Brown Ale', 7.3000001907, 1330, 'This is named after a song that sparked the idea to make this ale. Brown ales seem to be there, but not really there, do you know what I mean? Well that won\'t be the mistake with Wynona! We Voodooed out this style and added more that\'s what we say. Mildly hoppy and smooth with hints of chocolate and tons of brown malt to let you know it\'s there. Currently 7.3% by vol. \n\n\nBottle conditioned and Refermented. \n\n\nGet down with the brown, You\'ll be Happy You Did!!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5344, 'Belgian-Style Dubbel', 'Eislin Dubbel', 8, 402, 'The Dubbel is a nice brown creamy sweet, malty and strong ale. It is a traditional Belgian dubbel ale with a secondary fermentation. This complex ale has many flavor characteristics. At 8% abv you will notice a slight alcohol warmth at the finish .', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5345, 'American-Style India Pale Ale', 'Dead Man Walkin\' IPA', 6.4000000954, 409, 'This extra hoppy ale is characteristic of the classic pale ales. Using the very flavorful Mount Hood and Liberty hops we have created classic IPA goodness every sailor knows.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5352, 'American-Style Brown Ale', 'American Brown Ale', 6.5, 1250, 'Every winter 300 hardy Telemark skiers converge at Mt. Bohemia and the Porcupine Mountains ski areas for three days of back country touring, lift served high speed runs, chili cook offs, and dancing and beer drinking to Finnish Reggae, Bluegrass, or whatever else comes out of the woodwork. Steve brews this hoppy American Brown Ale in honor of his good friends and ski buddies from all over the Midwest and Canada who attend. Rich, malty, and dry-hopped to perfection. Available December through March.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5353, 'American-Style India Pale Ale', 'McGilligans IPA', 6.25, 1250, 'India Pale Ales, or IPAâ€™s, were brewed for British soldiers stationed in India. These ales needed to higher in alcohol and have more hops than normal to survive the long journey, and ours is no exception. Brewed with Belgian and German malts and dry hopped with loads of Cascades, our version honors our friend who had his own grueling journey involving a boat and cold water!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5354, 'American-Style Pale Ale', 'Paris-Roubaix Pale Ale', 5.25, 1250, 'Named after one of the longest running bicycle races in France and aptly nicknamed The Hell Of The North\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5355, 'Light American Wheat Ale or Lager', 'The Livery Dunkel Weizen', 5.25, 1250, 'This dark version of our Hefe Weizen retains the banana and clove flavors from the yeast and also has a richer malt flavor. Available November through April', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5356, 'Imperial or Double India Pale Ale', 'Double Paw', 8.5, 1250, 'A single malt and single hop version of an American Brewers classic style. This is definitely our hoppiest beer and quickly becoming a hop head cult classic!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5357, 'Herb and Spice Beer', 'Ginger Tom', 4, 474, 'An old fashioned style ginger beer brewed naturally with honey, malt, lemon and fresh root ginger, leaving you with a refreshing crisp zesty flavour.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5360, 'English-Style India Pale Ale', 'Hoppy Chick', 5.75, 1250, 'Named for mug club member and local jewelry maker, Angie Caldwell. English Maris Otter pale malt, English Crystal malt, and a blend of English and North American hops create this amber, hoppy IPA with just the right balance.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5361, 'Imperial or Double India Pale Ale', 'Cousin Jax', 9, 1250, 'Way up in the Keewenaw Peninsula in Michigans UP, Mt. Bohemia ski area has a powder run hidden at the top called Cousin Jack\" (named after the Cornish miners)that winds its\' way steeply through the rocks and trees. AAAHHH WINTER!!! Double the Belgian Malt', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5363, 'South German-Style Hefeweizen', 'The Livery Hefe Weizen', 5, 1250, 'A lightly hopped, refreshing, Bavarian style wheat ale that has hints of banana and clove in the finish. Available May until October.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5364, 'American-Style Amber/Red Ale', 'Thoms Special Ale', 6.25, 1250, 'This deep copper colored Ale is named after Steve\'s homebrew buddy Thom Phillips, who has had a part in helping to formulate some of the final Ale recipes at the Livery. Hints of caramel make their way through the aromatic bitterness of Centennial hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5366, 'American-Style Amber/Red Ale', 'Laughing Dragon', 6.25, 1250, 'Named for our friend and patron Tad Eastman, whose Laughing Dragon Tie-Dye studio puts out our fabulous shirts-each one an individual work of art signed by Tad! Reddish Amber in color, this beer has a very bold Chinook hop character.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5367, 'English-Style India Pale Ale', 'Herb Superb', 6, 1250, 'This India Pale Ale is brewed with English Maris Otter pale malt and Chocolate malt, giving it a dark color and is generously hopped with English Fuggles hops. Named for mug club member and brewery supporter Herb Caldwell.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5368, 'English-Style India Pale Ale', 'Bluejackets Best', 6.25, 1250, 'This is a single malt-English pale, and single hop-English Fuggles, India Pale Ale with a rich golden color and spicy finish. Named for brewery supporters John and Cindy Bundick, with reference to John\'s service on a submarine.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5369, 'Scotch Ale', 'The Livery Kilt Tilter', 9, 1250, 'Our version of a Scotch Ale is made with a portion of peat smoked barley. Very malty, with a subtle smoky finish. Also available in a barrel aged version.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5370, 'French & Belgian-Style Saison', 'Maillot Jaune', 5.5, 1250, 'Maillot Jaune, or Yellow Shirt\"', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5371, 'American-Style India Pale Ale', '555 IPA', 6.25, 1250, 'A true North American IPA! All North American malts, Northwest Mt. Hood hops, and American Ale yeast all blend for a perfectly balanced bitter ale.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5372, 'Belgian-Style Dark Strong Ale', 'Verchuosity', 12, 1250, 'Brewmaster Steve took his Belgian Amber Ale and re-fermented it in a merlot cask with 100# of sweet Michigan cherries from Husteds Farm Market. He than moved the resulting beer into another cask with 30# of tart cherries. 5 fermentations due to wild yeast and 9 months later, we have our version of a Belgian Kriek. Named after our good friend and musician, April Verch.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5375, 'Belgian-Style Dark Strong Ale', 'Local 2', 8.6999998093, 259, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5376, 'Extra Special Bitter', 'Barrel Aged Synapse ESB', 6, 1250, 'Extra Special Bitter, or ESB, were the benchmark beers for British brewers to serve to their special customers. Brewed with British pale and crystal malt, this reddish copper beer finishes with a subtle dose of English Fuggles hops.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5377, 'South German-Style Hefeweizen', 'Hopfen Weisse', 8.1999998093, 1023, 'Schneider & Brooklyner Hopfen-Weisse is a collaboration between brewmasters Hans-Peter Drexler of the Schneider Weissbier Brewery and Garrett Oliver of the Brooklyn Brewery. Garrett and Hans-Peter have long admired each others beers. Now together they bring you a new sensation, a pale weissbock robustly dry-hopped with the Hallertauer Saphir variety grown in the fields near the Schneider brewery. Hoppy, zesty and supremely refreshing, Scheider & Brooklyner Hopfen-Weisse is a delicious blend of Bavarian craftsmanship and American ingenuity.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5378, 'American-Style Amber/Red Ale', 'South County American Ale', 6.3299999237, 1168, 'This American style amber ale will be our opening release and house beer for SCBC. AmericAle is smooth and diverse all in one shot. With a medium malt body and fresh hop aroma we hope you enjoy drinking it as much as we do making it.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5380, 'Porter', 'Brigantine Smoked Porter', 8.5399999619, 1168, 'Our Brigantine Smoked Porter, much like the high seas, can be a wild ride. This beer is dark brown and full bodied with roasted tones and a hint of chocolate. As if that weren\'t enough it finishes with a smoked flavor that seems right at home during cooler months here in Southern York County.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5382, 'American-Style India Pale Ale', 'Big Swell IPA', 6.1999998093, 836, 'India Pale Ale was developed in Burton, England, as a Super-Premium\" hoppy pale ale around 1800. The extra strength in alcohol and hops helped preserve the beer on its long export journeys to India and beyond. The style developed a following worldwide. Its flavor begins with a smooth', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5383, 'Porter', 'Maui Coconut Porter', 5.6999998093, 836, 'Our Coconut Porter is a classic robust porter spiced with all natural toasted coconut.  It is black in color and crowned with a creamy, dark tan head.   It begins with a malty-toasted coconut aroma followed by a rich, silky feel with tastes of dark malt, chocolate, and hints of coffee.  It then finishes with flavors of  toasted coconut and hoppy spice to balance the finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5385, 'German-Style Pilsener', 'Budweiser Budvar (Czechvar)', 5, 245, 'Our Czech Premium Lager is beer for light beer lovers. The most gentle heads of the high quality Å½atec hop, virgin clear natural water and granules of selected species of Moravian barley make it the beverage of real experts. \n\n\nThe 700-year long tradition in production of ÄŒeskÃ© BudÄ›jovice beer and the unique, 90-day period of maturity increase its unique character. You can taste Budweiser Budvar Czech Premium Lager with all your senses. First of all you will delight your eyes with its beautiful colour and rich dense foam, then you will feel the fine aroma of the hops, in your palm you will stroke the dewy glass and, in the end, you will taste the fine to medium strong bitterness. You will remember well, our perfect lager.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5391, 'American-Style India Pale Ale', '75 Minute IPA', 7.5, 459, 'Johnny Cask has entered the building! Weâ€™ve retrofitted a 15 barrel tank to perfectly produce a very special cask conditioned ale (so, we have a little time to play around during winters at the Delaware coast). This beer, known as Dogfish Head 75 Minute IPA is a blend of 60 and 90 Minute IPAs with a special whole leaf cascade dry-hopping session.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5392, 'American-Style India Pale Ale', 'Six Rivers IPA', 7.1999998093, 1148, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5393, 'Porter', 'King\'s Peak Porter', 4, 1304, 'King\'s Peak Porter is deep mahogany in color and has a full-bodied malty flavor. Hints of chocolate malt are easily detectable.\n\n\nTopped with a tan creamy head, this beer has a definite hop character that nicely balances its mild bitterness. \n\n\nKing\'s Peak is the highest point in the state of Utah at 13,528 feet, located in northeastern Utah in the Uinta Mountains.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5395, 'American-Style Pale Ale', 'Back Road Ale', 5, 63, 'Back Road Brewery\'s flagship beer, and first original recipe of 1997, is also our house beer.  This mildly hopped copper colored brew has a rich smooth taste.  Hop varieties such as Styrian Goldings and English Fuggles are used to balance the beers maltiness.  A technique called dry hopping is used after fermentation to add fresh herbal aroma.  It is a great session beer and was made to satisfy a wide variety of palates.  Drink and enjoy LaPorte County\'s first production craft beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5396, 'American-Style Stout', 'Belle Gunness Stout', 5, 63, 'A notorious drink that\'s as dark as its\' namesakes history!!  It is named after the infamous LaPorte, Indiana serial killer herself, Belle Gunness of 1908.  This Irish-style dry stout is amazingly smooth to drink.  You will taste chocolate, coffee and roasty flavors in every sip.  Don\'t be afraid of the dark.  A true dark beer lover would die to try it.  It definitely would not kill you to dig some up.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5397, 'American-Style Pale Ale', 'Back Road American Pale Ale', 5, 63, 'If you are looking for a straight shooting quaffable beer with plenty of hops, this is it.  Straw colored from plump North American 2-row malted barley and deliciously hopped with Cascades from the USA, any patriot can enjoy this beer.  Cascade hops have a pleasant taste and fresh smell best described as florally or mildly citrusy.  A nice balance is struck between the malt and hops.  So how about it Yank?  Put that capitalism to work and buy some today!', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5398, 'American-Style India Pale Ale', 'Midwest IPA', 7.1999998093, 63, 'It means India Pale Ale.  It\'s all about the hops on this one.  Brewers use hops to add bitterness and aroma to their beers.  If not for hops your beer would be too sweet.  Our IPA uses plenty of Chinook and Columbus hops from the Pacific Northwest.  The flavor can best be described as strong resiny grapefruit.  The long drawn out hop flavor is essential to the character of the beer.  So smack your buds on this one all you HOP HEADS.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5399, 'German-Style Pilsener', 'Millennium Lager', 5, 63, 'A classic pilsner.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5400, 'German-Style Oktoberfest', 'Maple City Gold', 7.1999998093, 63, 'No, there is no maple flavor here, just a great beer with a good name.  LaPorte, Indiana is known as the Maple City\" because of its trees.  This German style Oktoberfest lager beer has a golden copper color and intense malty flavor.  We make this beer 4 times a year', 1);
