INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5406, 'American-Style India Pale Ale', 'Crooked Tree IPA', 6, 426, 'Inspired by West Coast I.P.A.\'s, but brewed with Michigan style. The Crooked Tree is heavily dry hopped to give it a big aroma of pine and citrus. The flavors are big, yet very balanced between fresh hops and malt. Often described as grapefruit\" our hops give this beer an excellent fruit flavor that finishes dry', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5408, 'American-Style India Pale Ale', 'Tumbleweed IPA', 5.6999998093, 783, 'A GOLD MEDAL WINNER at the Great American Beer Festival, beating out 98 other IPA\'s and chosen as the best IPA in the country! It\'s amber color and incredible hoppy aroma will keep you coming back for more. R-U-HOPPY?', 1);
