INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5439, 'Sweet Stout', 'Steel Toe Stout', 5.4000000954, 1150, 'This traditional English Cream Stout is brewed with actual milk sugar to create a creamy and sweet brew. Jet black in color, the latte frothy head will make you mooo for more.', 1);
