INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5874, 'American-Style Stout', 'Coffee Stout', 5.6999998093, 1092, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5875, 'American-Style Pale Ale', 'Pale Ale', 4.4000000954, 1404, 'APPEARANCE: Amber red, bright\nPROCESS: Classic English\nHOPS: East Kent Goldings (UK), Northdown (UK), Pilgrim (UK)\nMALTS: 2-Row and Caramel Malted Barley\nYEAST: London Ale\nOG: 11.2\nSRM: 13.5', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5876, 'Baltic-Style Porter', 'Baltika 6', 7, 1405, 'appearance:  deep, dark brown to black, deep tan head\nsmell/taste: sweet, malty, roasted coffee, molasses\nfull-bodied, smooth, minimal carbonation', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5878, 'Classic Irish-Style Dry Stout', 'Guinness Draught', 4.3000001907, 49, '', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5879, 'Kellerbier - Ale', 'Summer Teeth', 5.3000001907, 1409, 'This traditional Bavarian-style kellerbier originated from the small artisanal breweries of Franconia, where it is still a favorite in the local beer gardens.  Ours is served unfiltered with a crisp, smooth finish that taste like sunshine.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5880, 'Imperial or Double Red Ale', 'Dank', 9.1999998093, 1410, 'Anniversary offering, Imperial Red. Heavily hopped and oak aged, very balanced. 12 oz. bottles; 4 pack. ABV: 9.2%', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5881, 'American-Style Brown Ale', 'Dos Perros', 3.5, 1391, 'Many Mexican beer styles today are descendants of old Austrian styles, from when Austria ruled Mexico in the late 19th century. Our Dos Perros is made with German Munich malt, English Pale malt, and Chocolate malt, and hopped with Perle and Saaz hops. To lighten the body, as many Mexican brewers do, we add a small portion of flaked maize. The result is a wonderfully bready malt aroma, balanced with some maize sweetness and a noble hop finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5882, 'American-Style Strong Pale Ale', 'Pale Ale', 5.8000001907, 1391, 'A new version of an American classic. Our Yazoo Pale Ale bursts with spicy, citrusy hop aroma and flavor, coming from the newly discovered Amarillo hop. The wonderful hop aroma is balanced nicely with a toasty malt body, ending with a cleansing hop finish. Made with English Pale, Munich, Vienna, and Crystal malts, and generously hopped with Amarillo, Perle, and Cascade hops. Fermented with our English ale yeast.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5883, 'South German-Style Hefeweizen', 'Hefeweizen', 5, 1391, 'An authentic example of a Bavarian Hefeweizen.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5884, 'Porter', 'Sly Rye Porter', 5.6999998093, 1391, 'A rich, chocolaty English Porter with a clean finish. We use the finest floor-malted Maris Otter malts from England, the same malts used for the best single-malt scotch. A portion of malted rye gives a spicy, slightly dry finish.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5885, 'American-Style Stout', 'Onward Stout', 3.7999999523, 1391, 'The tan lace clings to the glass as you raise the pint to your lips. Close your eyes and smile as the rich espresso notes fade to a dry, roasted finish. Exceptionally smooth and satisfying. Made with English Pale malt, roasted barley, black patent malt, and flaked barley. Hopped with East Kent Goldings and Target hops, and fermented with our English ale yeast.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5886, 'Specialty Beer', 'Dark Element', 8.75, 1411, 'American Black Ale. Aromas of an American IPA, dark toffee and chocolate flavors without roasted bitterness.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5887, 'Ordinary Bitter', 'Finest Bitter', 4, 1412, 'A regular winner of awards for quality and flavour, and winner of the silver medal at the International Brewing Industry Awards 2002, this refreshing yet full-bodied bitter is a favourite with beer drinkers everywhere. The rich flavours of premium malt and goldings hops are unmistakable in this well balanced, traditionally brewed bitter.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5888, 'Special Bitter or Best Bitter', 'FA', 4.8000001907, 1412, 'FA is no small beer: despite its deceptively pale golden colour, it boasts a big, smooth flavour and strong punch. Brewed with the finest English malts, and conditioned in cask with dry hops to produce fresh hop aromas and a fuller flavour, delighting the mouth and stimulating the tongue.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5889, 'English-Style India Pale Ale', 'IPA', 3.5, 1412, 'IPA is one of Cains', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5890, 'English-Style Dark Mild Ale', 'Dark Mild', 3.2000000477, 1412, 'This is a smooth, full flavoured, truly dark mild with a rich creamy head. It has a full body and distinctive roasted malt taste, balanced by its fresh hop character. Complex flavours and aromas are achieved by blending deeply roasted malt and adding a selected blend of English hops, including dry hops, to the cask conditioned beer.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5891, 'Ordinary Bitter', '2008 Culture Beer', 5, 1412, 'A full, clean and refreshing pale coloured bitter with an abundance of citrus hop character created by the blend of dry and late hopping techniques.\n\nIt also has subtle hints of malt and fruit on aroma and palate which gives the beer a well rounded balance.', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5892, 'English-Style India Pale Ale', 'Iron Rail IPA', 7.1999998093, 1413, 'English Style India Pale Ale, Maris Otter 2-row, Canadian Honey Malt and Belgian Crystal.\nCentennial, Kent Golding, and Cascade are added Five times during the process, including Dry Hopping.\n\nAroma: Earthy, Citrusy, and Resinous\nTasting/Flavor: Orangy and Sugary Apricots\nFinish: Malt Sweetness Balanced by Dry Hops', 1);
INSERT INTO `beer` (`id`, `style`, `name`, `abv`, `brewery_id`, `description`, `approved`) VALUES (5894, 'Belgian-Style Tripel', 'Vivant Tripel', 9.25, 1416, 'A classic golden ale, with subtle esters of banana and bubblegum. This beer pours with a creamy head and ends with a sweetness of light Belgian candi sugars. Our brewmaster aged this ale for an extended period to round out the flavors. A great traditional Belgian brew.', 1);
