INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('UFO Raspberry Hefeweizen', 4978, 634, 11, 121, 4.8000001907, 'We have added natural raspberry flavor to our UFO Hefeweizen to create this beer.  Consistent with the hefeweizen style, this beer is unfiltered and cloudy with a solid foamy head.  UFO Raspberry has a distinctive, hazy rose color.  The scent of fresh raspberries hits the nose immediately, along with a subtle bready aroma from the wheat and yeast.  The body is light and the unfiltered yeast provides a soft mouthfeel.  The taste of the fruit compliments the beer nicely, neither overwhelms the other.  There is a faint sweetness on the palate, which finishes cleanly in a semi-dry, tart finish.', 'Fruit Beer', 'Harpoon Brewery - Boston', '306 Northern Avenue', 'Boston', 'Massachusetts', 'United States', '', '42.3465, -71.0338', 'http://www.harpoonbrewery.com');
