INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Base Camp Golden Ale', 3559, 520, 3, 37, 4.9000000954, 'The lightest of FiftyFifty\'s ales, Base Camp is a thirst quenching delight. Pale straw to light gold in color, this beer has a mild malt flavor combined with light bitterness and hop flavor. This beer finishes dry and is very light and refreshing on the palate. Base Camp is a \"session beer\", meaning you can easily toss back a few in one sitting. So go ahead, pull up a chair and camp out for awhile.', 'Golden or Blonde Ale', 'Fifty Fifty Brewing Co.', '11197 Brockway Road', 'Truckee', 'California', 'United States', '', '39.322, -120.163', 'http://www.fiftyfiftybrewing.com/');
