INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('90 Shilling', 208, 935, 1, 7, 5.3000001907, 'We introduced 90 Shilling, our flagship beer, at our opening party in 1989. For a while, we\'d been wondering - what would happen if we lightened up the traditional Scottish ale? The result is an irresistibly smooth and delicious medium-bodied amber ale. The name 90 Shilling comes from the Scottish method of taxing beer. Only the highest quality beers were taxed 90 Shillings. A shilling was a British coin used from 1549 to 1982. We think you\'ll find this original ale brilliantly refreshing, and worth every shilling.', 'Scottish-Style Light Ale', 'Odell Brewing', '800 East Lincoln Avenue', 'Fort Collins', 'Colorado', 'United States', '', '40.5894, -105.063', '');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Moose Drool Brown Ale', 3762, 122, 2, 25, 5.3000001907, 'It\'s chocolate brown in color with a creamy texture. A malty beer with just enough hop presence to keep it from being too sweet. The aroma mostly comes from the malt with a hint of spice added by the hops. Moose Drool is brewed with pale, caramel, chocolate, and whole black malts; and Kent Goldings, Liberty, and Willamette hops. It has an original gravity of 13 degrees Plato, and is 4.2% alcohol by weight, 5.3% by volume.', 'Porter', 'Big Sky Brewing', '5417 Trumpeter Way', 'Missoula', 'Montana', 'United States', '', '46.9223, -114.073', '');
