INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Mocha Porter / New Porter', 4184, 1072, 2, 25, 5.3000001907, 'Dedicated to the chocolate lover in each of us. Mocha Porter was once known as New Porter, in honor of the town of Newport, Oregon and home of Rogue Ales. The January/February 1995 issue of Mens Health magazine features a bottle of Rogue New Porter (todays Mocha Porter) in the Fifth Annual Collection of Good Advise, Health News, Dire Warnings, Notable Folks and Unsolicited Opinion. New Porter is described as the Best New Beer for 1994! The caption reads: \"Oh, Hoppy Day: For a beer- drinking experience order up a bottle of this microbrew from Oregon.', 'Porter', 'Rogue Ales', '2320 SE OSU Drive', 'Newport', 'Oregon', 'United States', '', '44.6202, -124.052', 'http://www.rogue.com');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Alaskan Amber', 4079, 17, 1, 13, 5.3000001907, 'Alt. The name of this beer style comes from the German word \"alt\" meaning \"old\". This refers to the aging that alts undergo since they ferment more slowly and at colder temperatures than most ales. Slow fermentation helps condition the flavors in Alaskan Amber, contributing to its overall balance and smoothness.\n\n\nRichly malty and long on the palate, with just enough hop backing to make this beautiful amber colored \"alt\" style beer notably well balanced.\n\n\nAlaskan Amber is made from glacier-fed water and a generous blend of the finest quality European and Pacific Northwest hop varieties and premium two-row pale and specialty malts.  Our water originates in the 1,500 square-mile Juneau Ice Field and the more than 90 inches of rainfall we receive each year.', 'Old Ale', 'Alaskan Brewing', '5429 Shaune Drive', 'Juneau', 'Alaska', 'United States', '', '58.3573, -134.49', 'http://www.alaskanbeer.com/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Survival Stout', 5268, 678, 1, 21, 5.3000001907, 'Beer of the Ancients! Barley (Egyptian), Wheat (Mesopotamian), Oats (Egyptian), Amaranth (Aztec), Quinoa (Incan), Spelt (Mesopotamian), and Kamut (Egyptian) sustain the soul with a nutrients cultivated through the millennia. Finished with 15 pounds of cold-pressed Stumptown Hairbender espresso. Unlock the mystery entombed in darkness.', 'Oatmeal Stout', 'Hopworks Urban Brewery', '2944 SE Powell Blvd', 'Portland', 'Oregon', 'United States', '', '45.4969, -122.635', 'http://hopworksbeer.com/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Sun Dog Amber Wheat', 5002, 44, 11, 116, 5.3000001907, 'Sun Dog is an unfiltered beer, with a naturally cloudy appearance and fuller texture which allows it to stand up to spicy foods like Thai noodle salads and Cuban sandwiches.  The beer is best served in a tall, wide-mouthed glass which opens up the aromas of the beer and showcases its beautiful long-lasting head of white foam.', 'Light American Wheat Ale or Lager', 'Anheuser-Busch', 'One Busch Place', 'Saint Louis', 'Missouri', 'United States', '', '38.5983, -90.209', 'http://www.anheuser-busch.com/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Michelob Hop Hound Amber Wheat', 5224, 44, 11, 116, 5.3000001907, 'Unfiltered American amber wheat ale brewed with a blend of imported and domestic hops for a balanced slightly citrus hop aroma and caramel malty taste.', 'Light American Wheat Ale or Lager', 'Anheuser-Busch', 'One Busch Place', 'Saint Louis', 'Missouri', 'United States', '', '38.5983, -90.209', 'http://www.anheuser-busch.com/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Erdinger Weizen', 4518, 1019, 11, 116, 5.3000001907, 'The famous Bavarian specialty\n\n\nErdinger Weissbier \'crystal clear\' is particularly welcome as a tasty thirst-quencher for hot summer days.\n\n\nThis specialty wheat beer gets its \'crystal clear\' appearance from a particularly long filtration process, known in the industry as \"fine filtration\", whereby the beer becomes completely clear.\n\n\nThis gives Erdinger Weissbier \'crystal clear\' its sparkling and refreshing character - which also makes it the ideal wheat beer for warm summer days.\n\n\nBrewed in strict accordance with our own traditional recipe, Erdinger Weissbier \'crystal clear\' meets the highest quality standards and satisfies the most sophisticated palate.', 'Light American Wheat Ale or Lager', 'Privatbrauerei Erdinger Weissbru', 'Franz-Brombach-Strae 1-20', 'Erding', 'Bayern', 'Germany', '', '48.3153, 11.8911', '');
