INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Masala Mama IPA', 1171, 869, 3, 31, 5.9000000954, 'Our brewers believe an American IPA should be full of American hops. Masala Mama uses three different West Coast varieties, over five hop additions. This copper-colored ale is not strictly about hops; American pale barley and several caramel malts balance the flavor for a green, toasted caramel finish. The British realized an abundance of hops will help preserve beer â€¦ we realized hops can preserve our customers.', 'American-Style India Pale Ale', 'Minneapolis Town Hall Brewery', '1430 Washington Avenue South', 'Minneapolis', 'Minnesota', 'United States', '', '44.9732, -93.2479', 'http://www.townhallbrewery.com/');
