INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Anniversary Ale 2007', 4276, 1142, 3, 31, 5.9000000954, 'From the Sierra Nevada Brewing Co. website:\n\nEach fall we celebrate our anniversary as one of Americaâ€™s craft brewing pioneers with a special beer.  This year, in celebration of our 27th year, we have made this beer available to the public for the first time, and aptly named it Anniversary Ale.    \n\n\nAnniversary Ale is an American-style IPA featuring Cascade hops, the signature hop used in our Pale Ale. The beer has a pronounced pine and citrus hop aroma balanced by the sweetness of two-row pale and caramel malts. The result is an unusually well-balanced IPA that proves an IPA can be both assertive and elegant. Anniversary Ale is a medium-bodied, well-hopped ale that finishes with a slight malt sweetness.', 'American-Style India Pale Ale', 'Sierra Nevada Brewing Co.', '1075 East 20th Street', 'Chico', 'California', 'United States', '', '39.7246, -121.816', 'http://www.sierranevada.com/');
