INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Truly Honest Ale', 5169, 1081, 3, 33, 6, 'Our signature brew that started it all. This beer is made with four different specialty malts including two foreign caramel malts, a pale malt, and a toasted malt. These malts impart a subtle toasted/sweet taste. This malty brew is than graced with floral and aroma hops which balance the maltiness of this brew and leaves behind a smooth finish on the palate. Top notch ingredients and a truly \"Honest\" taste makes this a seriously drinkable ale, unlike many other bitter American ales.', 'American-Style Amber/Red Ale', 'Roy Pitz Brewing Company', '140 North Third Street', 'Chambersburg', 'Pennsylvania', 'United States', '', '39.939, -77.6566', 'http://roypitz.com/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Geist Bock', 126, 584, 7, 90, 6.0999999046, 'This is one of our rare lagers brewed in the style of the German Heiliges Geist Bock, or Holy Ghost Bock. The name refers to the lighter nature of the beer as opposed to the darker and heavier Doppel Bock and Mai Bock also brewed during the spring in Germany. There is a rich malt flavor, a firm German style lager head, and a clean lagered finish to this beer.  Available on draft or in 12 oz. bottles.', 'Traditional German-Style Bock', 'Golden Valley Brewery and Pub', '980 NE Fourth Street', 'McMinnville', 'Oregon', 'United States', '', '45.2104, -123.189', 'http://www.goldenvalleybrewery.com/');
