INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Misery Bay IPA', 4786, 507, 3, 31, 6.5, 'The view from Oliver Perry Monument across Lake Erieâ€™s historic Misery Bay provides a constant reminder of the hardships endured during the Battle of Lake Erie. Misery Bay IPA is brewed as a tribute to Misery Bay and Graveyard Pond, final resting place for many brave sailors and soldiers. Misery Bay IPA offers a complex malt profile loaded with American hops at 75 IBUâ€™s, and finishes at 6.5 % alcohol by volume.', 'American-Style India Pale Ale', 'Erie Brewing Company', '1213 Veshecco Drive', 'Erie', 'Pennsylvania', 'United States', '', '42.1112, -80.113', 'http://www.eriebrewingco.com');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Southern Tier IPA', 4039, 1173, 3, 31, 6.5, 'IPA stands for India Pale Ale and ours is an American version of the classic style.  IPA\'s namesake lies in its colonial roots.  British soldiers on their way to India drank a lot of beer, but found it would go stale on the long voyages.  Meanwhile, brewers knew that by adding more hops the beer would stay fresh.  Voila!  A new style was born and it is one we are proud to brew.  Southern Tier IPA is triple-hopped on its journey to your glass for a fully aromatic experience.', 'American-Style India Pale Ale', 'Southern Tier Brewing Co', '2051A Stoneman Circle', 'Lakewood', 'New York', 'United States', '', '42.1008, -79.3357', 'http://www.southerntierbrewing.com/');
