INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Donner Party Porter', 1714, 520, 2, 25, 6.6999998093, 'Sustenance to get you through those long, cold winters, the Donner Party Porter is another of FiftyFifty\'s flagship beers. Reminiscent of dark chocolate, espresso, and dark dried fruits with a long complex finish, this beer is good for one of those nights where you\'ve dug yourself into a snow bank because the ski trip didn\'t go quite as planned. Deep brown with mahogany highlights, the hops and malt are very well balanced leaving one to contemplate the myriad of flavors as the beer warms. Moderate to slightly heavy, this beer is heaven in a glass.', 'Porter', 'Fifty Fifty Brewing Co.', '11197 Brockway Road', 'Truckee', 'California', 'United States', '', '39.322, -120.163', 'http://www.fiftyfiftybrewing.com/');
