INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Flashback Anniversary Ale', 5435, 160, 3, 38, 6.8000001907, 'Flashback Anniversary Ale is an India-Brown Ale with 6.8% ABV. This is the first beer weâ€™ve made here that uses one single hop variety (Cascade) in the recipe in five separate additions. The fresh Cascade hop aroma and flavor is perfectly balanced with the dark roasted grains, making Flashback a very unique beer. Weâ€™re calling it an India Brown Ale to help illustrate its flavor to the consumer. Itâ€™s hoppy like an IPA but dark and roasty like a Brown Ale. Put them together and voila! Flashback at its finest!', 'American-Style Brown Ale', 'Boulder Beer Company', '2880 Wilderness Place', 'Boulder', 'Colorado', 'United States', '', '40.0267, -105.248', 'http://boulderbeer.com/');
