INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Tournay Black', 5677, 172, 5, 66, 7.5999999046, 'Tournay Black is a bottle conditioned, Belgian stout brewed by Brasserie de Cazeau which is a farm-brewery located in the south of Belgium.  It was originally brewed as a winter ale under the name Tournay de Noel, but later became a year-round release.  They didn\'t provide much specific detail about how it was brewed except to say that it is made with water, malts, candi-sugar, hops, and yeast.  It is 7.6% ABV with a recommended drinking temperature of +/- 50 degrees.  The brewer states that the tasting notes are of, \"roasted malts, coffee, bitter chocolate, earth, and cigar ash.\"  Well, I am a prodigious cigar smoker and I can\'t say that I would want to consume the ash.', 'Belgian-Style Dark Strong Ale', 'Brasserie de Cazeau', '', '', '', 'Belgium', '', '', '');
