INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Big Sound Scotch Ale', 5728, 379, 1, 15, 8.5, 'Big Sound Scotch Ale is dedicated to our good buddy Gino, the most punk rock bagpiper you\'ll ever meet, and the rest of the men and women of Tampa Bay Pipes and Drums. Brown in color, Big Sound has huge notes of dark sweet toffee with underlying mild notes of coffee in the aroma. The flavor starts with a slight note of cherry and then opens into a Goliath of a malt character with notes of dark sweet toffee, slight hints of coffee and mild notes of toasted bread in the finish. Big Sound Scotch Ale pairs well with Haggis, Highland Games, Huge Heads and Enormous Pillows and of course Bagpipe music.', 'Scotch Ale', 'Cigar City Brewing', '3924 West Spruce Street Suite A', 'Tampa', 'Florida', 'United States', '', '27.9587, -82.5093', 'http://cigarcitybeer.com/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Skullsplitter Ale', 3108, 953, 1, 15, 8.5, 'Skull Splitter is our strongest ale: which is named after Thorfinn Einarsson who was the 7th Viking Earl of Orkney. Sophisticated, satiny smooth with a deceptively light character, it is a tribute to our colourful forbear.\n\n\nOn the nose, this strong beer has a fruity malt character, with hints of dark fruit, spicy hop, dates and figs.\n\n\nOn the palate, rich and complex with sweet toasted malt, molasses, fresh and dried fruit and hints of warming spices.', 'Scotch Ale', 'Orkney Brewery', 'Quoyloo', 'Orkney', 'Scotland', 'United Kingdom', '', '59.0697, -3.3135', 'http://www.orkneybrewery.co.uk/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Heavy Seas The Great Pumpkin Imperial Pumpkin Ale', 5589, 385, 11, 123, 8.5, 'The secret is in the 2.5 pounds of spice per barrel for this fall brew. We add the pumpkin during the mash at precisely the right time to create just the perfect balance of malt, hops, pumpkin and spice.\n\n\nestimated ABV 8.5% estimated IBU 25\n\nMalt, Hops, Pumpkin & our Secret Spices', 'Pumpkin Beer', 'Clipper City Brewing Co.', '4615-B Hollins Ferry Road', 'Baltimore', 'Maryland', 'United States', '', '39.2308, -76.6751', 'http://www.ccbeer.com/');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Final Absolution', 4910, 463, 5, 72, 8.5, 'Dragonmead\'s signature product! This is the ultimate Belgian style. The very high gravity of this beer is balanced by the smoothness of its finish. Banana and Clove aromas come from the Belgian yeast strain combining with the generous dose of Belgian Candi Sugar. The Saaz hops help to give this beer a balanced bitterness with no noticeable hop aroma.', 'Other Belgian-Style Ales', 'Dragonmead Microbrewery', '14600 East Eleven Mile Road', 'Warren', 'Michigan', 'United States', '', '42.493, -82.9753', '');
