INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Hardcore IPA', 5707, 241, 3, 32, 9, 'This little bottle has a grandiloquent story to tell.\n\n\n2,204 malted Maris Otter grains gave all they had to offer the world to provide the robustly delicate toffee malt canvas for the ensuing epic.\n\n\n6 Hop Cones willingly sacrificed themselves in fiery cauldron that is our brew kettle to ensure your mouth is left feeling punished and puckering for more.\n\n\n9,900,000,000 yeast cells frantically fermented their little hearts out as the sugars were magically turned into alcohol in the dark depths of our fermentation tanks.\n\n\nThis explicit ale has more hops and bitterness that any other beer brewed in the UK. This is an extreme beer rollercoaster for freaks, gypsies and international chess superstars.', 'Imperial or Double India Pale Ale', 'BrewDog Ltd', 'AB43 8UE', 'Fraserburgh', '', 'United Kingdom', '', '57.683, -2.003', 'http://brewdog.com/');
