INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('John Barleycorn Barleywine Style Ale', 2008, 810, 3, 35, 9, 'Our Seasonal Offering. We honor the Harvest & Holidays each year by brewing a traditional Barleywine style ale, using a variety of ingredients. The Label, created by local artist Janis Taylor, is a folksy woodcut based on the tale of John Barleycorn in verse.\n\n\nBrewed in small 10-barrel batches with Certified Organic barley malt, this crimson hued ale has a sweet caramel nose with a zesty spicy, citrus taste. It has a slight hop finish and leaves a warm, tingling sensation on the tongue. A great winter warmer!\n\nStarting Gravity 	1.098\n\nFinish Gravity 	1.020\n\nABV 	9.5\n\nIBU 	96\n\n\n-http://www.madriverbrewing.com/pages/brews/john_barleycorn.html', 'American-Style Barley Wine Ale', 'Mad River Brewing', '195 Taylor Way', 'Blue Lake', 'California', 'United States', '', '40.879, -123.993', '');
INSERT INTO `temp_beer_brewery` (`name`, `id`, `brewery_id`, `cat_id`, `style_id`, `alcohol_by_volume`, `description`, `style`, `brewer`, `address`, `city`, `state`, `country`, `zip`, `coordinates`, `website`) VALUES ('Imperial Red', 4206, 1072, 3, 33, 9, 'Imperial Red is the newest addition to Rogues award winning Imperial Family of XS products. The Imperial Red recipe originates from Rogues Eugene City Brewery and is being produced in Newport for distribution (note, initially during 2007 in kegs only to JLS accounts. A 750-ml ceramic swing-top bottle and 13.2 gallon kegs will be available in 2008.) Imperial Red has already garnered international acclaim and awards, including a Bronze Medal at the 2006 Great American Beer Festiva and recently it was named Grand Champion at the US Beer Tasting Championships! \n\n\nDeep copper chestnut color with toffee, brown sugar, nuts, and spice amomas. A rich supple entry leads to an off-dry medium body of tangy raisins and citrus marmalade, bittersweet chocolate, and mild spice flavors. It finishes with a long nutty, fruity fade. \n\n\nImperial Red is brewed with Crystal, Chocolate, Munich and two-row Pale Malts; Palisade and Aroma Crystal hops, Pacman Yeast, and Free Range Coastal Waters.', 'American-Style Amber/Red Ale', 'Rogue Ales', '2320 SE OSU Drive', 'Newport', 'Oregon', 'United States', '', '44.6202, -124.052', 'http://www.rogue.com');
